#!/usr/bin/python
# coding=utf-8
#
# <bitbar.title>Alexa rank checker</bitbar.title>
# <bitbar.version>v1.1</bitbar.version>
# <bitbar.author>Luca Cappelletti</bitbar.author>
# <bitbar.author.github>LucaCappelletti94</bitbar.author.github>
# <bitbar.desc>Displays Alexa rank from given websites</bitbar.desc>
# <bitbar.image>http://i.imgur.com/30YjoCF.jpg</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>
#
# by Luca Cappelletti

import urllib
import sys
import xml.etree.ElementTree as ET
import time
import json
import os
from urlparse import urlparse

reload(sys)
sys.setdefaultencoding('utf8')


class Alexa:
    api_url = "http://data.alexa.com/data"
    is_online_test_url = "http://google.com"
    cache_file_dir = "/tmp/alexa_rank_cache"
    cache_file_name = "/tmp/alexa_rank_cache/alexa_cache.json"
    popularity_icon = " 🌐 "
    black_popularity_icon = " ♥ "
    delta_icon = " ⇕ "
    default_flag_icon = "⚑"
    flags_icons = {
        "AU": "🇦🇺",
        "CA": "🇨🇦",
        "CH": "🇨🇭",
        "CN": "🇨🇳",
        "DE": "🇩🇪",
        "ES": "🇪🇸",
        "FR": "🇫🇷",
        "GB": "🇬🇧",
        "IL": "🇮🇱",
        "IT": "🇮🇹",
        "JP": "🇯🇵",
        "KN": "🇰🇳",
        "KR": "🇰🇷",
        "NO": "🇳🇴",
        "NP": "🇳🇵",
        "PS": "🇵🇸",
        "QA": "🇶🇦",
        "RU": "🇷🇺",
        "SS": "🇸🇸",
        "US": "🇺🇸"
    }

    OFFLINE = 0
    UNRETRIEVED_DATA = 1
    INVALID_JSON = 2
    UNEXISTING_FILE = 3
    INVALID_URLS = 4
    UNEXISTING_ERROR_ID = 5

    error_messages = {
        OFFLINE: {
            "en": "You are offline.",
            "de": "Du bist Offline.",
            "it": "Sei offline."
        },
        UNRETRIEVED_DATA: {
            "en": "Unable to get data for %s.",
            "it": "Impossibile recuperare dati per %s."
        },
        INVALID_JSON: {
            "en": "The file %s does not contain valid json.",
            "it": "Il file %s non contiene json valido."
        },
        UNEXISTING_FILE: {
            "en": "The file %s does not exist.",
            "it": "Il file %s non esiste."
        },
        INVALID_URLS: {
            "en": "No valid urls were provided.",
            "it": "Non son stati inseriti url validi."
        },
        UNEXISTING_ERROR_ID: {
            "en": "The given error_id %s is not valid",
            "it": "L'error_id %s fornito non è valido"
        }
    }

    website_url_list = []
    alexa_data = {}
    cli = 10
    cache = True
    max_offline_wait = 10
    user_language = "en"
    default_user_language = "en"
    polling_interval = 60 * 60  # One hour
    show_global = True
    show_top_country = True
    show_delta = False
    use_black_icons = False

    def __init__(self, website_url_list, cache=None, polling_interval=None, show_global=None, show_top_country=None, show_delta=None, use_black_icons=None, max_offline_wait=None, user_language=None):

        if cache != None:
            self.cache = cache

        if polling_interval != None:
            self.polling_interval = polling_interval

        if show_global != None:
            self.show_global = show_global

        if show_top_country != None:
            self.show_top_country = show_top_country

        if show_delta != None:
            self.show_delta = show_delta

        if use_black_icons != None:
            self.use_black_icons = use_black_icons

        if max_offline_wait != None:
            self.max_offline_wait = max_offline_wait

        if user_language != None:
            self.user_language = user_language

        for website_url in website_url_list:
            if self.is_valid_url(website_url):
                self.website_url_list.append(website_url)

        if len(self.website_url_list) == 0:
            print(self.get_error_message(self.INVALID_URLS))
            sys.exit()

        self.update()

    @classmethod
    def from_url(cls, website_url, cache=None, polling_interval=None, show_global=None, show_top_country=None, show_delta=None, use_black_icons=None, max_offline_wait=None, user_language=None):
        return cls([website_url], cache, polling_interval, show_global, show_top_country, show_delta, use_black_icons)

    def get_error_message(self, error_id):
        if error_id in self.error_messages:
            if self.user_language in self.error_messages[error_id]:
                return self.error_messages[error_id][self.user_language]
            return self.error_messages[error_id][self.default_user_language]
        print(self.get_error_message(self.UNEXISTING_ERROR_ID), error_id)
        sys.exit(0)

    def get_data(self, website_url, data):
        return self.alexa_data[website_url][data]

    def extract(self, raw_data, key, attribute):
        return raw_data.find(key).get(attribute)

    def extract_delta(self, raw_data):
        try:
            return int(self.extract(raw_data, "RANK", "DELTA"))
        except AttributeError:
            return 0

    def extract_rank(self, raw_data):
        try:
            return int(self.extract(raw_data, "REACH", "RANK"))
        except AttributeError:
            return 0

    def extract_country_rank(self, raw_data):
        try:
            return int(self.extract(raw_data, "COUNTRY", "RANK"))
        except AttributeError:
            return 0

    def extract_country_code(self, raw_data):
        try:
            return self.extract(raw_data, "COUNTRY", "CODE")
        except AttributeError:
            return ""

    def extract_country_name(self, raw_data):
        try:
            return self.extract(raw_data, "COUNTRY", "NAME")
        except AttributeError:
            return ""

    def extract_url(self, raw_data):
        try:
            return self.extract(raw_data, "POPULARITY", "URL").strip("/")
        except AttributeError:
            return ""

    def build_url(self, website_url):
        return self.api_url + "?cli=" + str(self.cli) + "&url=" + website_url

    def read_from_url(self, url):
        try:
            return urllib.urlopen(url).read()
        except IOError:
            return None

    def get_alexa_data(self, website_url):
        request_url = self.build_url(website_url)
        data_xml = self.read_from_url(request_url)
        if data_xml == None:
            print(self.get_error_message(self.UNRETRIEVED_DATA), self.clean_url(website_url))
            sys.exit()
        return ET.ElementTree(ET.fromstring(data_xml)).getroot().find("SD")

    def update_url_data(self, website_url):
        raw_data = self.get_alexa_data(website_url)
        self.alexa_data[website_url] = {
            "delta": self.extract_delta(raw_data),
            "global_rank": self.extract_rank(raw_data),
            "top_country_rank": self.extract_country_rank(raw_data),
            "top_country_code": self.extract_country_code(raw_data),
            "top_country_name": self.extract_country_name(raw_data),
            "url": self.extract_url(raw_data),
            "last_update": time.time()
        }

    def save_data_to_cache(self):
        if not os.path.exists(self.cache_file_dir):
            os.makedirs(self.cache_file_dir)
        with open(self.cache_file_name, 'w') as outfile:
            json.dump(self.alexa_data, outfile)

    def load_data_from_cache(self):
        if os.path.exists(self.cache_file_name):
            with open(self.cache_file_name, 'r') as outfile:
                try:
                    return json.load(outfile)
                except ValueError:
                    os.remove(self.cache_file_name)
                    pass
        return {}

    def is_valid_url(self, website_url):
        test = urlparse(website_url)
        if test.netloc == "":
            return False
        return True

    def is_user_online(self):
        if self.read_from_url(self.is_online_test_url) == None:
            return False
        return True

    def is_url_cached(self, website_url):
        if (website_url in self.alexa_data):
            if(self.get_data(website_url, "last_update") > time.time() - self.polling_interval):
                return True
        return False

    def update(self):
        if self.cache:
            self.alexa_data = self.load_data_from_cache()
        if self.is_user_online():
            for website_url in self.website_url_list:
                if not self.is_url_cached(website_url):
                    self.update_url_data(website_url)
        if(len(self.alexa_data) == 0):
            seconds_waited = 0
            while(not self.is_user_online() and self.max_offline_wait > seconds_waited):
                time.sleep(1)
                seconds_waited = seconds_waited + 1
            if(self.max_offline_wait > seconds_waited):
                self.update()
            else:
                print(self.get_error_message(self.OFFLINE))
                sys.exit()
        if self.cache:
            self.save_data_to_cache()

    def get_global_alexa_rank(self, website_url):
        return self.get_data(website_url, "global_rank")

    def get_top_country_alexa_rank(self, website_url):
        return self.get_data(website_url, "top_country_rank")

    def get_alexa_delta(self, website_url):
        return self.get_data(website_url, "delta")

    def get_top_country_alexa_code(self, website_url):
        return self.get_data(website_url, "top_country_code")

    def get_popularity_icon(self):
        if self.use_black_icons == False:
            return self.popularity_icon
        return self.black_popularity_icon

    def get_flag_icon(self, code):
        if self.use_black_icons == False and code in self.flags_icons:
            return self.flags_icons[code]
        return self.default_flag_icon + " " + code + ": "

    def clean_url(self, website_url):
        if website_url not in self.alexa_data or self.alexa_data[website_url]["url"] == "":
            website_url = website_url.replace("http://", "")
            website_url = website_url.replace("https://", "")
            website_url = website_url.replace("www.", "")
            return website_url
        return self.alexa_data[website_url]["url"]

    def build_bitbar(self):
        for website_url in self.website_url_list:
            bitbar = self.clean_url(website_url) + ":"
            if self.show_global:
                bitbar = bitbar + self.get_popularity_icon() + str(self.get_global_alexa_rank(website_url)) + " "
            if self.show_top_country:
                bitbar = bitbar + self.get_flag_icon(self.get_top_country_alexa_code(website_url)) + \
                    str(self.get_top_country_alexa_rank(website_url))
            if self.show_delta:
                bitbar = bitbar + self.delta_icon + str(self.get_alexa_delta(website_url))
            print(bitbar)
            print("---")

###################
#  USAGE EXAMPLES #
###################

# With single url
# myAlexa = Alexa.from_url("https://twitchtimer.com")

# Full parameters bonanza
#
# cache: put this to true to cache to /tmp/alexa_rank_cache/alexa_cache.json
# polling_interval: seconds of duration of caching file
# show_global: put this to true to show the global rank
# show_top_country: put this to true to show the top country rank
# show_delta: put this to true to show how the website rank has varied in the last period
# use_black_icons: for those who do not like color in their menu bar
# max_offline_wait: how many seconds should be waited brefore triyng again to read data and eventually show an "You are offline" error.
# user_language: the language in which errors and messages should be shown, if available
#
# myAlexa = Alexa(["twitchtimer.com", "google.com"], cache=True, polling_interval=60*60, show_global=True, show_top_country=True, show_delta=False, use_black_icons=False, max_offline_wait=15, user_language="en")

myAlexa = Alexa(["https://twitchtimer.com"], use_black_icons=True, show_top_country=False)
myAlexa.build_bitbar()

#!/usr/bin/env /usr/local/bin/node


/* jshint esversion: 6 */

/*
 * <bitbar.title>Cloudflare Development Mode</bitbar.title>
 * <bitbar.version>v1.0</bitbar.version>
 * <bitbar.author>Aaron Crawford</bitbar.author>
 * <bitbar.author.github>aaroncrawford</bitbar.author.github>
 * <bitbar.image>https://i.imgur.com/nKXgmxg.png</bitbar.image>
 * <bitbar.desc>Enable or Disable Development Mode from BitBar.  Plugin will need to be edited to include your API key and email to work.</bitbar.desc>
 * <bitbar.dependencies>node</bitbar.dependencies>
 */

// CHANGE THESE AREAS
let email = 'CHANGE@ME.com'; // Cloudflare email
let key = 'CHANGE ME'; // Global API Key, not Origin CA Key - Located under "My Profile"
//


var https = require('https');
var data = {
    zones: []
};

var headers = {
    'X-Auth-Email': email,
    'X-Auth-Key': key,
    'Content-Type': 'application/json'
};

function getZones() {
    var options = {
        host: 'api.cloudflare.com',
        path: '/client/v4/zones?per_page=50',
        method: 'GET',
        headers: headers
    };
    return new Promise((resolve, reject) => {
        var res = https.request(options, (res) => {
            var responseString = '';

            res.setEncoding('utf-8');

            res.on('data', function(data) {
                responseString += data;
            });

            res.on('end', function() {
                var parse = JSON.parse(responseString);
                parse.result.map((val, i) => {
                    if(val.development_mode > 0) {
                        dev = true;
                    }
                    else {
                        dev = false;
                    }
                    data.zones.push({name:val.name, id:val.id, dev:dev});
                });
                resolve();
            });

        });
        res.end();
    });
}

function flipSwitch(status) {
    var flag = (status == true) ? 'on' : 'off';
    var options = {
        host: 'api.cloudflare.com',
        path: `/client/v4/zones/${process.argv[3]}/settings/development_mode`,
        method: 'PATCH',
        headers: headers
    };

    return new Promise((resolve, reject) => {
        var postData = { value: flag };

        var res = https.request(options, (res) => {
            var body = [];
            var responseString = '';

            res.setEncoding('utf-8');
            res.on('data', function(data) {
                responseString += data;
            });
            res.on('end', function() {
                var parse = JSON.parse(responseString);
                resolve();
            });
            res.on('error', function(err) {
                reject(err);
            });
        });
        res.write(JSON.stringify(postData));
        res.end();
    });
}

console.log('⚙️');
console.log('---');

if(process.argv[2] == 'triggerUpdate') {
    var status = (process.argv[4] == 'Enable') ? true : false;
    flipSwitch(status)
    .then(getZones())
    .then(() => {
        data.zones.map((val, i) => {
            var status = (val.dev) ? 'Disable' : 'Enable';
            var color = (val.dev) ? 'red' : 'black';
            console.log(`${val.name}| color=${color}`);
            console.log(`--${status}| bash=${process.argv[0]} param1=${process.argv[1]} param2=triggerUpdate param3=${val.id} param4=${status} refresh=true terminal=false`);
        });
    });

}
else {
    getZones().then(() => {
        data.zones.map((val, i) => {
            var status = (val.dev) ? 'Disable' : 'Enable';
            var color = (val.dev) ? 'red' : 'black';
            console.log(`${val.name}| color=${color}`);
            console.log(`--${status}| bash=${process.argv[0]} param1=${process.argv[1]} param2=triggerUpdate param3=${val.id} param4=${status} refresh=true terminal=false`);
        });
    });
}

#!/usr/bin/env /usr/local/bin/node
/* jshint esversion:9 */
const https = require('https');

/* EDIT HERE */
const USER = '';
const PASSWORD = '';
const ORGANIZATION_NAME = '';
const PROJECT_ID = '';
/* DON'T EDIT BELOW */
// <bitbar.title>Codeship</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Gil Barbara</bitbar.author>
// <bitbar.author.github>gilbarbara</bitbar.author.github>
// <bitbar.desc>List recent builds.</bitbar.desc>
// <bitbar.dependencies>node</bitbar.dependencies>
// <bitbar.abouturl>https://github.com/gilbarbara/bitbar-plugins</bitbar.abouturl>
const BASE_URL = 'api.codeship.com';
const PROJECT_URL = `https://app.codeship.com/projects/${PROJECT_ID}`;
const AUTH = Buffer.from(`${USER}:${PASSWORD}`).toString('base64');
const COLORS = {
  success: '#42A86F',
  testing: '#607192',
  error: '#D12C3F',
  infrastructure_failure: '#9E212F',
  stopped: '#C4CDCE',
  waiting: '#3FCBDA',
};

let ACCESS_TOKEN;
let ORGANIZATION_UUID;
let PROJECT_UUID;

const ICON = '';
const RELOAD_ICON = 'iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAAmElEQVR4AY3SJdYCYRhA4V+BRGY5rAF3ZwvkiSyANLugYgUtOJsg4s7LxV3uOU8a+fTrom9EUMEAfZQRwDEFuwxIQ9CEetCBIIk4BF/fSKMPL26LYgHZoQgEXjxKgQCgCpqfvQwaQMVtOhhv3X7wURU08apvxGB5v2giFwTmT7bVigly+H11cAnUIChAD3p+NbrIwotv7NoAffg2NR6lsPIAAAAASUVORK5CYII=';

function request(options = {}) {
  const OPTIONS = {
    hostname: BASE_URL,
    path: `/v2${options.path || '/auth'}`,
    port: 443,
    method: options.method || 'GET',
    headers: {
      ...options.headers,
    },
  };

  return new Promise((resolve, reject) => {
    const req = https.request(OPTIONS, (response) => {
      const { headers, statusCode } = response;

      if (statusCode < 200 || statusCode > 299) {
        reject(new Error(`Request failed - status code: ${response.statusCode}`));
      }

      const isJSON = headers['content-type'].includes('application/json');

      // temporary data holder
      const body = [];
      // on every content chunk, push it to the data array
      response.on('data', chunk => body.push(chunk));
      // we are done, resolve promise with those joined chunks
      response.on('end', () => {
        const content = body.join('');
        resolve(isJSON ? JSON.parse(content) : content);
      });
    });

    // handle connection errors of the request
    req.on('error', err => reject(err));
    req.end();
  });
}

function timeSince(dateString) {
  const date = new Date(dateString);
  const seconds = Math.floor((new Date() - date) / 1000);
  let intervalType;

  let interval = Math.floor(seconds / 31536000);
  if (interval >= 1) {
    intervalType = 'year';
  }
  else {
    interval = Math.floor(seconds / 2592000);
    if (interval >= 1) {
      intervalType = 'month';
    }
    else {
      interval = Math.floor(seconds / 86400);
      if (interval >= 1) {
        intervalType = 'day';
      }
      else {
        interval = Math.floor(seconds / 3600);
        if (interval >= 1) {
          intervalType = 'hour';
        }
        else {
          interval = Math.floor(seconds / 60);
          if (interval >= 1) {
            intervalType = 'minute';
          }
          else {
            interval = seconds;
            intervalType = 'second';
          }
        }
      }
    }
  }

  if (interval > 1 || interval === 0) {
    intervalType += 's';
  }

  return `${interval} ${intervalType}`;
}

function formatTitle(build) {
  return `${build.branch} | href=${PROJECT_URL} color=${COLORS[build.status]}`;
}

function formatDate(build) {
  return `${timeSince(build.finished_at)} ago - (${build.username}) | size=12`;
}

function formatBuild(build) {
  return [
    formatTitle(build),
    formatDate(build),
  ].join('\n');
}

function handleResponse(body) {
  const content = body.map(formatBuild).join('\n---\n');
  const output = [
    `|image=${ICON}`,
    content,
    `RELOAD | image=${RELOAD_ICON} refresh=true`,
  ];
  console.log(output.join('\n---\n'));
}

const login = () => request({ method: 'POST', headers: { Authorization: `Basic ${AUTH}` } })
  .then((d) => {
    if (d.access_token) {
      ACCESS_TOKEN = d.access_token;

      const organization = d.organizations.find(o => o.name === ORGANIZATION_NAME);

      if (!organization) {
        throw new Error(`Organization "${ORGANIZATION_NAME}" not found`);
      }

      ORGANIZATION_UUID = organization.uuid;
    }
  })
  .catch((error) => {
    throw new Error(`[Login] ${error.message}`);
  });

const getProjects = () => request({
  path: `/organizations/${ORGANIZATION_UUID}/projects`,
  headers: { Authorization: `Bearer ${ACCESS_TOKEN}` },
});

const getBuilds = () => request({
  path: `/organizations/${ORGANIZATION_UUID}/projects/${PROJECT_UUID}/builds`,
  headers: { Authorization: `Bearer ${ACCESS_TOKEN}` },
});

login()
  .then(() => {
    getProjects()
      .then((d) => {
        const project = d.projects.find(p => p.id === PROJECT_ID);

        if (!project) {
          throw new Error(`Project "${PROJECT_ID}" not found`);
        }

        PROJECT_UUID = project.uuid;
      })
      .then(() => getBuilds())
      .then(d => handleResponse(d.builds.filter((b, i) => i < 15)))
      .catch(err => console.log(err.toString()));
  })
  .catch(err => console.log(err.toString()));

#!/usr/bin/env python
# -*- coding: utf-8 -*-

# <bitbar.title>Codeship</bitbar.title>
# <bitbar.version>v1.0.0</bitbar.version>
# <bitbar.author>Daniel Seripap</bitbar.author>
# <bitbar.author.github>seripap</bitbar.author.github>
# <bitbar.desc>Latest build status for all projects in Codeship</bitbar.desc>
# <bitbar.image>https://cloud.githubusercontent.com/assets/683200/16809335/fadc3746-48ed-11e6-8c86-517dd94412ff.png</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>

import json
import urllib2

# Get it here: https://codeship.com/user/edit
API_KEY = ''

BASE_URL = 'https://codeship.com/api/v1/projects.json?api_key=' + API_KEY
COLORS = {
  'success': '#42A86F',
  'testing': '#607192',
  'error': '#D12C3F',
  'stopped': '#C4CDCE'
}

def getData():
  try:
    return json.load(urllib2.urlopen(BASE_URL))
  except urllib2.URLError:
    return False

def generateMenu(data):
  if data is False:
    print 'Build data unavailable'
    return False

  for project in data['projects']:
    print project['repository_name'] + ' | href=' + 'https://codeship.com/projects/' + str(project['id'])
    print '---'
    for build in project['builds']:
      print '- ' + build['branch'] + ': ' + build['status']  + ' | color=' + COLORS[build['status']] + ' href=' + 'https://codeship.com/projects/' + str(build['project_id']) + '/builds/' + str(build['id'])
    print '---'

print 'Codeship'
print '---'
generateMenu(getData());

#!/usr/bin/env python
# -*- coding: utf-8 -*-

# <bitbar.title>Codeship Single Project</bitbar.title>
# <bitbar.version>v1.0.0</bitbar.version>
# <bitbar.author>Daniel Seripap</bitbar.author>
# <bitbar.author.github>seripap</bitbar.author.github>
# <bitbar.desc>Latest build status for individual Codeship project</bitbar.desc>
# <bitbar.image>https://cloud.githubusercontent.com/assets/683200/16809236/8fffa28c-48ed-11e6-9060-e42992612f50.png</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>

import json
import urllib2

# Get it here: https://codeship.com/user/edit
API_KEY = ''
# Modify accordingly
PROJECT_ID = 0

BASE_URL = 'https://codeship.com/api/v1/projects.json?api_key=' + API_KEY
HREF_URL = 'https://codeship.com/projects/' + str(PROJECT_ID) + '/builds/'
COLORS = {
  'success': '#42A86F',
  'testing': '#607192',
  'error': '#D12C3F',  
  'stopped': '#C4CDCE'
}

def getData():
  try:
    return json.load(urllib2.urlopen(BASE_URL))
  except urllib2.URLError:
    return False

def generate(data):
  header = []
  menu = []
  if data is False:
    print 'Build data unavailable'
    return False

  for project in data['projects']:
    if PROJECT_ID == project['id']:
      index = 0;
      latest = False;
      for build in project['builds']:
        menu.append('- ' + build['branch'] + ': ' + build['status'] + ' | color=' + COLORS[build['status']] + ' href=' + HREF_URL + str(build['id']))
        if index == 0:
          index = 1
          latest = COLORS[build['status']]

      header.append(project['repository_name'] + ' | color=' + latest)
      header.append('---')

  for line in header:
      print line.encode('utf-8')
  for line in menu:
      print line.encode('utf-8')

generate(getData());

#!/usr/bin/env /usr/local/bin/node

/* jshint esversion: 6 */

/*
 * <bitbar.title>DigitalOcean</bitbar.title>
 * <bitbar.version>v1.0</bitbar.version>
 * <bitbar.author>Hasit Mistry</bitbar.author>
 * <bitbar.author.github>hasit</bitbar.author.github>
 * <bitbar.image>http://i.imgur.com/1YTWFSn.png</bitbar.image>
 * <bitbar.desc>View status of DigitalOcean Droplets</bitbar.desc>
 * <bitbar.dependencies>node</bitbar.dependencies>
 */

var https = require('https');

var hostname = 'api.digitalocean.com';

// Add your Digital Ocean token.
// If you have different DO teams you can add multiple tokens: ['DO_TOKEN_1', 'DO_TOKEN_2', ...]
var DOTokens = ['DO_TOKEN'];

var menuBarIcon = '💧';
var startMenu = '---';

console.log(menuBarIcon);
console.log(startMenu);

for (var i = 0; i < DOTokens.length; i++) {
    if (DOTokens.length == 1 && DOTokens[0] === 'DO_TOKEN') {
        console.log('DO token is missing!');
    } else {
        tokenIndex = i;
        getDroplets();
    }
}

function getDroplets() {
    performRequest('/v2/droplets', 'GET', function(data) {
        var droplets = data.droplets;

        for (var i = 0; i < droplets.length; i++) {
            var status = '';
            var date = new Date(droplets[i].created_at);
            var humanDate = date.getFullYear() + '/' + date.getMonth() + 1 + '/' + date.getDate() + ' ' + date.getHours() + ':' + date.getMinutes();

            if (droplets[i].status === 'active') {
                status = '';
            }

            printBitBarLine(droplets[i].name, ['color=black', 'image=' + status]);

            // Open in browser and console
            printBitBarLine('--Open in browser', ['href=https://cloud.digitalocean.com/droplets/' + droplets[i].id]);
            printBitBarLine('--Connect to console', ['href=https://cloud.digitalocean.com/droplets/' + droplets[i].id + '/console?no_layout=true']);

            // Droplet creation date and time
            printBitBarLine('-----');
            printBitBarLine('--Created');
            printBitBarLine('--' + humanDate, ['color=black']);

            // Droplet IPv4 address
            printBitBarLine('-----');
            printBitBarLine('--IP Address');
            printBitBarLine('--' + droplets[i].networks.v4[0].ip_address, ['color=black']);

            // Droplet region
            printBitBarLine('-----');
            printBitBarLine('--Region');
            printBitBarLine('--' + droplets[i].region.name, ['color=black']);

            // Droplet details - image, memory, storage, vcpus
            printBitBarLine('-----');
            printBitBarLine('--Details');
            printBitBarLine('--Image: ' + droplets[i].image.distribution + droplets[i].image.name, ['color=black']);
            printBitBarLine('--Memory: ' + droplets[i].memory + 'MB', ['color=black']);
            printBitBarLine('--Storage: ' + droplets[i].disk + 'GB', ['color=black']);
            printBitBarLine('--CPUs: ' + droplets[i].vcpus, ['color=black']);
        }
    });
}

function printBitBarLine(title, args) {
    var lineArgs = [];

    if (title !== '-----') {
        for (var i in args) {
            lineArgs.push(args[i]);
        }

        if (lineArgs.length !== 0) {
            title = title + '|';
        }
    }

    console.log(title + lineArgs.join(' '));
}

function performRequest(endpoint, method, success) {
    var headers = {};

    headers = {
        'Authorization': 'Bearer ' + DOTokens[tokenIndex],
        'Content-Type': 'application/json'
    };

    var options = {
        host: hostname,
        path: endpoint,
        method: method,
        headers: headers
    };

    var req = https.request(options, function(res) {
        res.setEncoding('utf-8');

        var responseString = '';

        res.on('data', function(data) {
            responseString += data;
        });

        res.on('end', function() {
            var responseObject = JSON.parse(responseString);
            success(responseObject);
        });
    });

    req.end();

    req.on('error', function(err) {
        console.log(err);
    });
}

#!/usr/bin/env python
# -*- coding: utf-8 -*-

# <bitbar.title>GitHub Notifications</bitbar.title>
# <bitbar.version>v3.0.2</bitbar.version>
# <bitbar.author>Matt Sephton, Keith Cirkel, John Flesch</bitbar.author>
# <bitbar.author.github>flesch</bitbar.author.github>
# <bitbar.desc>GitHub (and GitHub:Enterprise) notifications in your menu bar!</bitbar.desc>
# <bitbar.image>https://i.imgur.com/hW7dw9E.png</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>

import json
import urllib2
import os
import sys
import re
from itertools import groupby

# GitHub.com
github_api_key = os.getenv( 'GITHUB_TOKEN', 'Enter your GitHub.com Personal Access Token here...' )

# GitHub:Enterprise (optional)
enterprise_api_key = os.getenv( 'GITHUB_ENTERPRISE_TOKEN', 'Enter your GitHub:Enterprise Personal Access Token here...' )
enterprise_api_url = os.getenv( 'GITHUB_ENTERPRISE_API', 'https://github.example.com/api/v3' )

active = '#4078C0'
inactive = '#7d7d7d'

# Utility Functions

def plural( word, n ):
    return str(n) + ' ' + (word + 's' if n > 1 else word)

def get_dict_subset( thedict, *keys ):
    return dict([ (key, thedict[key]) for key in keys if key in thedict ])

def print_bitbar_line( title, **kwargs ):
    print title + ' | ' + ( ' '.join( [ '{}={}'.format( k, v ) for k, v in kwargs.items() ] ) )

def make_github_request( url, method='GET', data=None, enterprise = False ):
    try:
        api_key = enterprise_api_key if enterprise else github_api_key
        headers = {
            'Authorization': 'token ' + api_key,
            'Accept': 'application/json',
        }
        if data is not None:
            data = json.dumps(data)
            headers['Content-Type'] = 'application/json'
            headers['Contnet-Length'] = len(data)
        request = urllib2.Request( url, headers=headers )
        request.get_method = lambda: method
        response = urllib2.urlopen( request, data )
        return json.load( response ) if response.headers.get('content-length', 0) > 0 else {}
    except Exception:
        return None

def get_notifications( enterprise ):
    url = '%s/notifications' % (enterprise_api_url if enterprise else 'https://api.github.com')
    return make_github_request( url, enterprise=enterprise ) or []

def print_notifications( notifications, enterprise=False ):
    notifications = sorted( notifications, key=lambda notification: notification['repository']['full_name'] )
    for repo, repo_notifications in groupby( notifications, key=lambda notification: notification['repository']['full_name'] ):
        if repo:
            repo_notifications = list( repo_notifications )
            print_bitbar_line( title=repo )
            print_bitbar_line(
                title='{title} - Mark {count} As Read'.format( title=repo, count=len( repo_notifications ) ),
                alternate='true',
                refresh='true',
                bash=__file__,
                terminal='false',
                param1='readrepo',
                param2=repo,
                param3='--enterprise' if enterprise else None
            )
            for notification in repo_notifications:
                formatted_notification = format_notification( notification )
                print_bitbar_line( refresh='true', **get_dict_subset( formatted_notification, 'title', 'href', 'image', 'templateImage' ) )
                print_bitbar_line(
                    refresh='true',
                    title='%s - Mark As Read' % formatted_notification['title'],
                    alternate='true',
                    bash=__file__,
                    terminal='false',
                    param1='readthread',
                    param2=formatted_notification['thread'],
                    param3='--enterprise' if enterprise else None,
                    **get_dict_subset( formatted_notification, 'image', 'templateImage' )
                )

def format_notification( notification ):
    type = notification['subject']['type']
    formatted = {
        'thread': notification['url'],
        'title': notification['subject']['title'].encode('utf-8'),
        'href': notification['subject']['url'],
        'image': 'iVBORw0KGgoAAAANSUhEUgAAAA4AAAAQCAYAAAAmlE46AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEwAACxMBAJqcGAAAA',
    }
    if len(formatted['title']) > 90:
        formatted['title'] = formatted['title'][:79] + '…'
    formatted['title'] = formatted['title'].replace('|','-')
    latest_comment_url = notification.get( 'subject', {} ).get( 'latest_comment_url', None )
    typejson = make_github_request( formatted['href'] )
    if latest_comment_url:
        formatted['href'] = ( make_github_request( latest_comment_url ) or {} ).get( 'html_url', formatted['href'] )
    # Try to hack a web-viewable URL if the last check failed
    if formatted['href']:
        formatted['href'] = re.sub( 'api\.|api/v3/|repos/', '', re.sub( '(pull|commit)s', ur'\1', formatted['href'] ) )
    if (type == 'PullRequest'):
        if typejson and typejson['merged']:
            formatted['image'] += ''
        elif typejson and typejson['state'] == 'closed':
            formatted['image'] += ''
        else:
            formatted['image'] += ''
        if typejson and typejson.get( 'user', {} ).get( 'login', None ):
            formatted['title'] += ' (by @{})'.format(typejson['user']['login'])
    elif (type == 'RepositoryInvitation'):
        formatted['image'] = '';
        formatted['templateImage'] = formatted.pop('image');
        formatted['href'] = 'https://github.com/{}/invitations'.format(notification['repository']['full_name'])
    elif (type == 'Issue'):
        if typejson and typejson['state'] == 'closed':
            formatted['image'] += ''
        else:
            formatted['image'] += ''
    elif (type == 'Commit'):
        formatted['image'] += 'HhJREFUKJHl0LEKwkAQBNCH3yIaf05S+VUqmh8ykFoUYn8WbnEc8a7XgYVlmNkdhv/EDgNmvHDFtmXq8ETCiFvsD2xqxksI9xnXB3cqxamYceHgVOpWC6JUi/QNQxj7jDsEd6wZ83KmLOId69bXzqekOeas0eiv4g3q4SY7NY1R2gAAAABJRU5ErkJggg=='
        formatted['templateImage'] = formatted.pop('image')
    elif (type == 'Release'):
        formatted['image'] += 'JdJREFUKJGl0DsKwkAUBdDTRgvFHbgmNyLY+QWzKxM/kK2kSKc70MIIQ0ziqBceA/dxinn8mSkKVMGUmH+CBWaNboQjdn2wqt97Pa8kNd5+C0O86YNdSZC34RLjCJxhHZYLXDCIxKuwTHGOwBNcm2WKUw9OcMCybZl6XjHpQOs30cB5gKNQiDPPP0WjV/a4aVwxNsNfUGce7P8k4XgVPSYAAAAASUVORK5CYII='
        formatted['templateImage'] = formatted.pop('image')
    return formatted

if len(sys.argv) > 1:
    command = sys.argv[1]
    args = sys.argv[2:]
    enterprise=False
    if ('--enterprise' in args):
        enterprise=True
        args.remove( '--enterprise' )
    if command == 'readrepo':
        url = '%s/repos/%s/notifications' % (enterprise_api_url if enterprise else 'https://api.github.com', args[0])
        print 'Marking %s as read' % url
        make_github_request( url=url, method='PUT', data={}, enterprise=enterprise )
    elif command == 'readthread':
        url = args[0]
        print 'Marking %s as read' % url
        make_github_request( url=url, method='PATCH', data={}, enterprise=enterprise )

else:
    is_github_defined = len( github_api_key ) == 40
    is_github_enterprise_defined = len( enterprise_api_key ) == 40
    github_notifications = get_notifications( enterprise=False ) if is_github_defined else []
    enterprise_notifications = get_notifications( enterprise=True ) if is_github_enterprise_defined else []
    has_notifications = len( github_notifications ) + len( enterprise_notifications )
    color = active if has_notifications else inactive

    if (has_notifications):
        print_bitbar_line(
            title=u'\u25CF'.encode( 'utf-8' ),
            color=color
        )
        print '---'
    else:
        print ''
        exit(0)

    print_bitbar_line( title='Refresh', refresh='true' )

    if is_github_defined:
        if len( github_notifications ):
            print_bitbar_line(
                title=( u'GitHub \u2014 %s' % plural( 'notification', len( github_notifications ) ) ).encode( 'utf-8' ),
                color=active,
                href='https://github.com/notifications',
            )
            print_notifications( github_notifications )
        else:
            print_bitbar_line(
                title=u'GitHub \u2014 No new notifications'.encode( 'utf-8' ),
                color=inactive,
                href='https://github.com',
            )

    if is_github_enterprise_defined:
        if len( enterprise_notifications ):
            if is_github_defined:
                print '---'
            print_bitbar_line(
                title=( u'GitHub:Enterprise \u2014 %s' % plural( 'notification', len( enterprise_notifications ) ) ).encode( 'utf-8' ),
                color=active,
                href='%s/notifications' % re.sub( '/api/v3', '',  enterprise_api_url ),
            )
            print_notifications( enterprise_notifications, enterprise=True )
        else:
            print '---'
            print_bitbar_line(
                title=u'GitHub:Enterprise \u2014 No new notifications',
                color=inactive,
            )

#!/usr/bin/env /usr/local/bin/node
// jshint asi:true
// jshint esversion: 8
// <bitbar.title>GitLab Merge Requests</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Simeon Cheeseman</bitbar.author>
// <bitbar.author.github>simeonc</bitbar.author.github>
// <bitbar.desc>List of your assigned and created merge requests.
// The menu bar shows a count of MRs requiring your approval (🔎), WIP MRs (🛠️), Un-mergable MRs (⛔), MRs with failed pipelines (⚠️), MRs with unresolved discussions (🚧), MRs with running pipeline (🚀), MRs pending approval (💬) and Approved/Ready to Merge (❇️).
// Each MR in the dropdown is grouped by project and displays `<Unmergable> "Title"; "Pipeline Status" : "Approval Status"`, clicking on an MR opens it in the browser</bitbar.desc>
// <bitbar.dependencies>node.js</bitbar.dependencies>
// <bitbar.image>https://i.imgur.com/t0TtQXO.png</bitbar.image>

/**
 * Information
 *
 * This was inspired by the work of Shelton Koskie (@eightygrit), who made the
 * first version of the "GITLAB Projects" for API v4.
 *
 * @see   GitLab API Documentation    https://docs.gitlab.com/ee/api/README.html
 * @see   Create GitLab Access Token  https://gitlab.com/profile/personal_access_tokens
 * @see   BitBar Node Module Docs     https://github.com/sindresorhus/bitbar
 */

/**
 * The domain your instance is hosted on. Leave the default if using gitlab.com
 *
 * @const       {string}
 */
const gitlab_domain = 'gitlab.com';

/**
 * Your private access token.
 *
 * @const       {string}
 * @see       Create a token  https://gitlab.com/profile/personal_access_tokens
 */
const private_token = '7Tkm2aLMxk8q-M9TaJz1';

/**
 * Font size of the project list
 *
 * @const       {string}
 */
const font_size = '15';

/**
 * Change this array to choose which statuses show and in what order on the menu bar.
 *
 * @type {string[]}
 */
const MENU_BAR_ORDER = [
  'newMR',
  'reviewer',
  'wip',
  'unmergable',
  'failed',
  'unresolved',
  'ciRunning',
  'pending',
  'approved'
];

/**
 * MAX length of the title string in the toolbar
 *
 * @type {number}
 */
const MAX_LENGTH = 60;

/////////////////////////////////////////////////////////////////////////
// Do not edit below this line unless you know what you're doing. :)  //
///////////////////////////////////////////////////////////////////////
let bitbar;
const fs = require('fs');
const path = require('path');
const SETTINGS_FILE_PATH = path.resolve(
  __dirname,
  './.gitlab_merge_requests.settings.json'
);

// Verify bitbar node module is available or try to install it globally.
try {
  bitbar = require('bitbar');
} catch (e) {
  try {
    bitbar = globalRequire('bitbar');
  } catch (e) {
    installBitbarModule();

    // Not catching error if one is thrown.
    bitbar = globalRequire('bitbar');
  }
}

// Converts the gitlab status to emoji
// see https://emojipedia.org/ for customisation
function stateIcon(status) {
  return {
    created: '💤',
    pending: '💤',
    running: '🚀',
    failed: '⚠️',
    success: '✅',
    skipped: '🚀',
    manual: '⏯️'
  }[status];
}

const gitlabIconBase64 =
  '';

function request(path) {
  const httpTransport = require('https');
  const responseEncoding = 'utf8';
  const httpOptions = {
    hostname: gitlab_domain,
    port: '443',
    path,
    method: 'GET',
    headers: { 'PRIVATE-TOKEN': private_token }
  };
  httpOptions.headers['User-Agent'] =
    'bitbar/gitlab_projects - node ' + process.version;

  return new Promise((resolve, reject) => {
    const request = httpTransport
    .request(httpOptions, (res) => {
      let responseBufs = [];
      let responseStr = '';

      res
      .on('data', (chunk) => {
        if (Buffer.isBuffer(chunk)) {
          responseBufs.push(chunk);
        } else {
          responseStr = responseStr + chunk;
        }
      })
      .on('end', () => {
        if (responseBufs.length > 0) {
          responseStr = Buffer.concat(responseBufs).toString(responseEncoding);
        } else {
          responseStr = responseStr;
        }

        resolve(JSON.parse(responseStr));
      });
    })
    .setTimeout(0)
    .on('error', (error) => {
      reject(error);
    });
    request.write('');
    request.end();
  });
}

const cachedProjectNames = {};
async function registerProject(project_id) {
  if (!cachedProjectNames[project_id]) {
    const project = await request(`/api/v4/projects/${project_id}`);
    cachedProjectNames[project_id] = project.name_with_namespace;
  }
}

async function getMRs() {
  if (!fs.existsSync(SETTINGS_FILE_PATH)) {
    const myUser = await request('/api/v4/user');
    fs.writeFileSync(
      SETTINGS_FILE_PATH,
      JSON.stringify({
        userId: myUser.id
      })
    );
  }

  const statusCount = {};
  function incrementStatusCount(key) {
    statusCount[key] = statusCount[key] || 0;
    statusCount[key] += 1;
  }

  const mergeRequestsByProjectId = {};

  const yesterday = new Date();
  yesterday.setDate(yesterday.getDate() - 1);
  const yesterdayParam = `${yesterday.getFullYear()}-${yesterday.getMonth() + 1}-${yesterday.getDate()}`;

  const { userId, todaysUUID, todaysExcludedEvents = [] } = JSON.parse(fs.readFileSync(SETTINGS_FILE_PATH));
  let excludedEvents = [];
  if (todaysUUID === yesterdayParam) {
    excludedEvents = todaysExcludedEvents;
  }

  const createdMRs = await request(
    '/api/v4/merge_requests?scope=created_by_me&state=opened'
  );
  const assignedMRs = await request(
    '/api/v4/merge_requests?scope=assigned_to_me&state=opened'
  );
  const approvalMRs = await request(
    `/api/v4/merge_requests?scope=all&state=opened&approver_ids[]=${userId}`
  );

  const allMRs = assignedMRs
  .concat(approvalMRs, createdMRs)
  .reduce((uniqueMRs, currentMR) => {
    if (!uniqueMRs.find(({ id }) => id === currentMR.id)) {
      uniqueMRs.push(currentMR);
    }
    return uniqueMRs;
  }, []);

  const bitbar = globalRequire('bitbar');

  if (allMRs.length === 0) {
    // Hide when no open MRs
    return bitbar([
      {
        image: gitlabIconBase64,
        text: `0 MR`,
        color: bitbar.darkMode ? 'white' : 'black',
        dropdown: true
      }
    ]);
  }


  await Promise.all(
    allMRs.map(async (mergeRequest) => {
      const {
        web_url,
        merge_when_pipeline_succeeds,
        merge_status,
        title,
        project_id,
        iid,
        blocking_discussions_resolved,
        work_in_progress,
        assignee,
        assignees,
        author,
        source_branch
      } = mergeRequest;
      excludedEvents.push(`${project_id}_${source_branch}`);

      const isReviewer =
        (!author || author.id !== userId) &&
        (!assignee || assignee.id !== userId) &&
        (!assignees || !assignees.find(({ id }) => id === userId));

      await registerProject(project_id);

      mergeRequestsByProjectId[project_id] =
        mergeRequestsByProjectId[project_id] || [];

      const pipelines = await request(
        `/api/v4/projects/${project_id}/merge_requests/${iid}/pipelines`
      );
      let pipelineStatus = '';
      if (pipelines.length) {
        pipelineStatus = stateIcon(pipelines[0].status);
      }
      const approvals = await request(
        `/api/v4/projects/${project_id}/merge_requests/${iid}/approval_state`
      );
      const isApproved = approvals.rules.reduce(
        (isApproved, { approved }) => isApproved && approved,
        true
      );
      const hasCurrentUserApproved = approvals.rules.reduce(
        (currentUserHasApproved, { approved_by }) =>
          currentUserHasApproved ||
          !!approved_by.find(({ id }) => id === userId),
        false
      );
      const canMerge = merge_status === 'can_be_merged';
      if (isReviewer && (work_in_progress || !canMerge || isApproved || hasCurrentUserApproved))
        return;
      let mergeStatus = '  ';
      if (!canMerge) {
        mergeStatus = '⛔ ';
      } else if (isReviewer) {
        mergeStatus = '🔎 ';
      }
      let approvalStatus = '💬';
      if (work_in_progress) {
        approvalStatus = '🛠️';
      } else if (!blocking_discussions_resolved) {
        approvalStatus = '🚧';
      } else if (isApproved) {
        approvalStatus = '❇️';
      }
      if (isReviewer) {
        incrementStatusCount('reviewer');
      } else if (work_in_progress) {
        incrementStatusCount('wip');
      } else if (merge_status !== 'can_be_merged') {
        incrementStatusCount('unmergeable');
      } else if (pipelines.length && pipelines[0].status === 'failed') {
        incrementStatusCount('failed');
      } else if (!blocking_discussions_resolved) {
        incrementStatusCount('unresolved');
      } else if (pipelines.length && pipelines[0].status === 'running') {
        incrementStatusCount('ciRunning');
      } else if (!isApproved) {
        incrementStatusCount('pending');
      } else {
        incrementStatusCount('approved');
      }

      let statusString = '';
      if (pipelineStatus || approvals.rules.length) {
        const statusStrings = [];
        if (pipelineStatus) statusStrings.push(pipelineStatus);
        if (approvals.rules.length) statusStrings.push(approvalStatus);
        statusString = `  ${statusStrings.join(' ')}`;
      }
      let trimmedTitle = title.substring(0, MAX_LENGTH);
      if (title.length > MAX_LENGTH) {
        trimmedTitle += '...';
      }
      mergeRequestsByProjectId[project_id].push(
        `${mergeStatus}!${iid}: ${trimmedTitle}${statusString} | href="${web_url}" size=${font_size}`
      );
    })
  );

  const todaysEvents = await request(
    `/api/v4/users/4557473/events?after=${yesterdayParam}&action=pushed`
  );
  for (let i = 0; i < todaysEvents.length; i += 1) {
    const { project_id, push_data: {action, ref, ref_type, commit_to} } = todaysEvents[i];
    if (excludedEvents.indexOf(`${project_id}_${ref}`) !== -1) continue;
    if (!(action === 'created' && ref_type === 'branch')) continue;

    const mrCheck = await request(`/api/v4/projects/${project_id}/repository/commits/${commit_to}/merge_requests`);
    if (mrCheck.length) {
      excludedEvents.push(`${project_id}_${ref}`);
      continue;
    }
    await registerProject(project_id);
    const { web_url } = await request(`/api/v4/projects/${project_id}`);
    const params = [
      `merge_request%5Bsource_branch%5D=${ref}`,
      `merge_request%5Bsource_project_id%5D=${project_id}`,
      `merge_request%5Btarget_project_id%5D=${project_id}`
    ];
    const createMRUrl = `${web_url}/-/merge_requests/new?${params.join('&')}`;
    mergeRequestsByProjectId[project_id] = mergeRequestsByProjectId[project_id] || [];
    mergeRequestsByProjectId[project_id].unshift(
      `📝: ${ref} | href="${createMRUrl}" size=${font_size}`
    );
    incrementStatusCount('newMR');
  }

  const content = [];

  const statusMap = {
    reviewer: '🔎',
    unmergable: '⛔',
    failed: '⚠️',
    ciRunning: '🚀',
    pending: '💬',
    approved: '❇️',
    wip: '🛠️',
    unresolved: '🚧',
    newMR: '📝'
  };
  const statusString = MENU_BAR_ORDER
  .map((key) => {
    if (statusCount[key]) {
      return `${statusMap[key]}[${statusCount[key]}]`;
    }
  })
  .filter((s) => !!s)
  .join(', ');

  content.push({
    image: gitlabIconBase64,
    text: `${statusString}`,
    color: bitbar.darkMode ? 'white' : 'black',
    dropdown: true
  });

  Object.keys(mergeRequestsByProjectId).forEach((projectId) => {
    if (!mergeRequestsByProjectId[projectId].length) return;
    content.push(bitbar.separator);
    content.push({
      text: cachedProjectNames[projectId]
    });
    mergeRequestsByProjectId[projectId].forEach((text) =>
      text ? content.push({ text }) : null
    );
  });

  // Update settings file for speed later
  fs.writeFileSync(
    SETTINGS_FILE_PATH,
    JSON.stringify({
      userId,
      todaysUUID: yesterdayParam,
      todaysExcludedEvents: excludedEvents.reduce((uniqueEvents, event) => {
        if (uniqueEvents.indexOf(event) === -1) {
          uniqueEvents.push(event);
        }
        return uniqueEvents;
      }, [])
    })
  );

  try {
    bitbar(content);
  } catch (error) {
    console.log(content);
    throw error;
  }
}

getMRs().catch((error) => {
  console.error(error);
});

/**
 * Sets up the ability to require global node packages.
 *
 * @return     {object}  Returns the required node package object
 */
function globalRequire(package) {
  const childProcess = require('child_process');
  const path = require('path');
  const fs = require('fs');
  const env = Object.assign({}, process.env);
  env.PATH = path.resolve('/usr/local/bin') + ':' + env.PATH;

  const globalNodeModulesDir =
    childProcess
    .execSync(npmBin() + ' root -g', { env: env })
    .toString()
    .trim() + '/';
  let packageDir = path.join(globalNodeModulesDir, package, '/');

  //find package required by older versions of npm
  if (!fs.existsSync(packageDir)) {
    packageDir = path.join(globalNodeModulesDir, 'npm/node_modules/', package);
  }

  // Package not found
  if (!fs.existsSync(packageDir)) {
    throw new Error("Cannot find global module '" + package + "'");
  }

  const packageMeta = JSON.parse(
    fs.readFileSync(path.join(packageDir, 'package.json')).toString()
  );
  const main = path.join(packageDir, packageMeta.files[0]);

  return require(main);
}

/**
 * Installs Bitbar node module if it doesn't exits.
 *
 * @see    BitBar node module on github    https://github.com/sindresorhus/bitbar
 */
function installBitbarModule() {
  // Allows one to run the npm command as if on the command line.
  const childProcess = require('child_process');
  const execSync = childProcess.execSync;
  const path = require('path');
  const fs = require('fs');

  const env = Object.assign({}, process.env);
  env.PATH = path.resolve('/usr/local/bin') + ':' + env.PATH;

  // Get the path to npm bin
  const npm = npmBin();

  // The install command
  const cmd = npm + ' install -g bitbar';

  console.log('Installing the BitBar Node module...');

  const output = execSync(cmd, {
    cwd: process.cwd(),
    env: env
  })
  .toString('utf8')
  .trim();

  console.log('Installation complete.');
}

/**
 * Gets the path to your npm executable.
 *
 * @return  {string}  The full path to your npm executable
 */
function npmBin() {
  const path = require('path');
  const childProcess = require('child_process');
  const execSync = childProcess.execSync;
  const env = Object.assign({}, process.env);
  env.PATH = path.resolve('/usr/local/bin') + ':' + env.PATH;
  const buffs = [];

  // Get the path to npm bin
  return execSync('which npm', { env: env })
  .toString('utf8')
  .trim();
}

