#!/usr/bin/php
<?php

// <bitbar.title>Pantheon - List Sites</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Dave Wikoff</bitbar.author>
// <bitbar.author.github>derimagia</bitbar.author.github>
// <bitbar.desc>List and manage all of your sites you are on in Pantheon.</bitbar.desc>
// <bitbar.dependencies>php, terminus</bitbar.dependencies>
// <bitbar.image>https://i.imgur.com/VYBizXY.png</bitbar.image>
// <bitbar.abouturl>https://github.com/derimagia/pantheon-bitbar</bitbar.abouturl>

define('TERMINUS_PATH', '/usr/local/bin/terminus');
define('CONFIG_PATH', '/tmp/pantheon-list-sites-config.json');
define('DEBUG_MODE', false);

$php = PHP_BINARY;
$script = escapeshellarg($argv[0]);
$directory = dirname(__FILE__);
$html_filename = pathinfo(__FILE__, PATHINFO_FILENAME) . '.dynamic.html';
$html_filepath = $directory . '/' . $html_filename;
$config = get_config();
$env_id = $config->env_id ? $config->env_id : 'dev';

if (!empty($argv[1]) && function_exists($argv[1])) {
  $args = $argv;
  // Shift the first 2 arguments
  array_shift($args);
  array_shift($args);
  call_user_func_array($argv[1], $args);
  exit(0);
}

$sites = terminus("sites list --cached");

if (!is_array($sites)) {
  echo 'Could not get site list. Did you auth using Terminus?';
  exit();
}

$symbolMap = [
  'dev' => '🔵',
  'test' => '⚫',
  'live' => '🔴',
];

$symbol = isset($symbolMap[$env_id]) ? $symbolMap[$env_id] : '';

$items = array(
  ['title'  => "Environment: $env_id -- $symbol", 'bash' => $php, 'param1' => $script, 'param2' => 'pantheon_switch_environment', 'param3' => $env_id, 'terminal' => 'false', 'refresh' => 'true'],
  '---',
);

foreach ($sites as $site) {
  $items[] = ['title' => $site->name, 'bash' => $php, 'param1' => $script, 'param2' => 'pantheon_open_site', 'param3' => $site->name, 'param4' => $env_id, 'terminal' => 'false'];
  if ($site->framework === 'drupal') {
    $items[] = ['title' => "$site->name -- 🔒", 'alternate' => 'true', 'bash' => $php, 'param1' => $script, 'param2' => 'drush_user_login', 'param3' => $site->name, 'param4' => $env_id, 'terminal' => 'true'];
  }
  $items[] = ['title' => '└ Pantheon Dashboard -- ⚡', 'bash' => $php, 'param1' => $script, 'param2' => 'pantheon_open_dashboard', 'param3' => $site->name, 'param4' => $env_id, 'terminal' => 'true'];
  $items[] = '---';
}

echo "⚡\n";
echo "---\n";

foreach ($items as $item) {
  if (is_array($item)) {
    $parts = [];
    foreach ($item as $param => $value) {
      $parts[] = $param . '="' . $value . '"';
    }
    $item = $item['title'] . ' | ' . implode(' ', $parts);
  }

  echo $item . "\n";
}
exit(0);

/**
 * Open the dashboard for a site
 */
function pantheon_open_dashboard($site_id) {
  return browser_open(terminus("site dashboard --print", $site_id));
}

/**
 * Login to the site as User 1 for Drupal
 */
function drush_user_login($site_id, $env_id) {
  $login_url = drush($site_id, $env_id, "user-login 1");
  return browser_open($login_url);
}

/**
 * Switch the environment
 */
function pantheon_switch_environment($current_env_id) {
  global $script, $env_id, $config;

  $environments = ['dev', 'test', 'live'];

  foreach ($environments as $env) {
    if ($env == $current_env_id) {
      break;
    }
  }

  $next_env = current($environments) ? current($environments) : 'dev';

  $config->env_id = $next_env;
  save_config($config);
}

/**
 * Returns the domain for a pantheon site.
 */
function pantheon_open_site($site_id, $env_id) {
  $alias = drush_get_alias($site_id, $env_id);

  $url = sprintf('%s://%s', 'https', $alias['uri']);

  return browser_open($url);
}

/**
 * Gets a Drush Alias for a Site ID / Env ID
 *
 * @return bool|array
 */
function drush_get_alias($site_id, $env_id) {
  $phpcode = terminus('sites aliases --print', NULL, NULL, FALSE);
  /* @var $aliases array[] */
  eval($phpcode);

  if (empty($aliases[$site_id . '.' . $env_id])) {
    echo 'Invalid Alias';
    exit(1);
  }

  return $aliases[$site_id . '.' . $env_id];
}

/**
 * Get a list of Pantheon Environments
 */
function pantheon_get_envs($site_id) {
  return terminus('site environments', $site_id);
}

/**
 * Opens a URL in the browser
 */
function browser_open($url) {
  passthru("open $url", $return_var);
  return $return_var;
}

/**
 * Run a terminus command
 * @return object
 */
function terminus($command, $site_id = null, $env_id = null, $json = TRUE) {
  $extras = ' --yes';
  if (!empty($json)) $extras .= ' --format=json';
  if (!empty($site_id)) $extras .= ' --site=' . $site_id;
  if (!empty($env_id)) $extras .= ' --env=' . $env_id;

  $command = TERMINUS_PATH . " {$command}{$extras}";

  $output = passthrough_return($command);
  return $json ? json_decode($output) : $output;
}

/**
 * We need to manually call drush because we need to add custom SSH options that terminus doesn't support.
 *
 * @return bool
 */
function drush($site_id, $env_id, $drush_command) {
  $alias = drush_get_alias($site_id, $env_id);
  $remote_host = $alias['remote-host'];
  $remote_user = $alias['remote-user'];
  $ssh_options = $alias['ssh-options'] . ' -o "StrictHostKeyChecking=no" -o "UserKnownHostsFile=/dev/null"';

  $command = escapeshellarg('drush ' . $drush_command);
  $command = 'ssh -T ' . $remote_user . '@' . $remote_host . ' ' . $ssh_options . ' ' . $command;

  return passthrough_return($command);
}

/**
 * Pass's the command through and returns it
 */
function passthrough_return($command) {
  ob_start();

  if (!DEBUG_MODE) {
    $command = $command . ' 2>/dev/null';
  }

  passthru($command);
  $output = ob_get_clean();

  if (DEBUG_MODE) {
    echo "----- DEBUG [$command] -----\n";
    echo "OUTPUT:\n";
    var_dump($output);
    echo "\n";
  }

  return $output;
}

/**
 * Gets the config for this plugin
 */
function get_config() {
  if (file_exists(CONFIG_PATH)) {
    $config = json_decode(file_get_contents(CONFIG_PATH));
  }
  return !empty($config) ? $config : new stdClass();
}

/**
 * Saves the config for this plugin
 */
function save_config($config) {
  return file_put_contents(CONFIG_PATH, json_encode($config));
}

#!/usr/bin/env  php
<?php
# <bitbar.title>RabbitMQ Status</bitbar.title>
# <bitbar.version>1.0</bitbar.version>
# <bitbar.author>Yann Milin</bitbar.author>
# <bitbar.author.github>ymilin</bitbar.author.github>
# <bitbar.desc>Keep an eye on your RabbitMQ server status from your menu bar !</bitbar.desc>
# <bitbar.image>http://i.imgur.com/Gz8FCJL.png</bitbar.image>
# <bitbar.dependencies>php >= 5.4.0</bitbar.dependencies>
# 
# RabbitMQ management plugin must be activated on server https://www.rabbitmq.com/management.html
# RabbitMQ Management HTTP API documentation https://raw.githack.com/rabbitmq/rabbitmq-management/rabbitmq_v3_6_0/priv/www/api/index.html

// Config
$baseUrl = "http://example.com";
$port = "15672";
$user = "guest";
$password = "guest";
$queuedMessageCountErrorLimit = 1000; // alert if queued messages higher than this, 0 = not active
// Config end

$statusBlackCircle = "⚫️";
$statusRedCircle = "🔴";
$statusWarning = "⚠️";
$colorBlack = "#333333";
$colorRed = "#FF0000";

$headers = [
    "Authorization: Basic " . base64_encode("$user:$password"),
    "content-type: application/json",
    "Accept: application/json",
    "Cache-Control: no-cache",
    "Pragma: no-cache",
];
$ch = curl_init();
$errors = [];

$uriOverview = "/api/overview";
$uriNodes = "/api/nodes";
$uriQueues = "/api/queues";

curl_setopt($ch, CURLOPT_PORT, $port);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
curl_setopt($ch, CURLOPT_TIMEOUT, 60);
curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);

/**
 * Executes a GET request and returns response body in array format
 *
 * @param $ch
 * @param $url
 * @return mixed
 */
$curl_exec = function (&$ch, $url) {
    curl_setopt($ch, CURLOPT_URL, $url);
    return json_decode(curl_exec($ch), true);
};

/**
 * Handles error message from the api, stopping the script on errors
 *
 * @param $ch
 * @param $response
 */
$curlHandleResponseError = function (&$ch, $response) {
    global $statusWarning, $colorRed;
    if (!$response) {
        $error = curl_error($ch);
        echo "$statusWarning Error |color=$colorRed\n";
        echo "---\n";
        echo "$error | color=$colorRed\n";
        exit;
    }
    if (array_key_exists('error', $response)) {
        echo "$statusWarning Error |color=$colorRed\n";
        echo "---\n";
        echo "error: {$response['error']}, reason: {$response['reason']} | color=$colorRed\n";
        exit;
    }
};

// api calls
$responseOverview = $curl_exec($ch, $baseUrl . $uriOverview);
$curlHandleResponseError($ch, $responseOverview);
$responseNodes = $curl_exec($ch, $baseUrl . $uriNodes);
$curlHandleResponseError($ch, $responseNodes);
$responseQueues = $curl_exec($ch, $baseUrl . $uriQueues);
$curlHandleResponseError($ch, $responseQueues);

// Extract values from responses
$messagesReady = $responseOverview['queue_totals']['messages_ready'];
$messagesUnacked = $responseOverview['queue_totals']['messages_unacknowledged'];
$messagesTotal = $responseOverview['queue_totals']['messages'];
$connections = $responseOverview['object_totals']['connections'];
$channels = $responseOverview['object_totals']['channels'];
$exchanges = $responseOverview['object_totals']['exchanges'];
$queuesCount = $responseOverview['object_totals']['queues'];
$consumers = $responseOverview['object_totals']['consumers'];
$rabbitMqVersion = $responseOverview['rabbitmq_version'];
$erlangVersion = $responseOverview['erlang_version'];

$statusIcon = $statusBlackCircle;
$nodes = [];
foreach ($responseNodes as $responseNode) {
    $nodes[] = [
        'name' => $responseNode['name'],
        'running' => $responseNode['running'],
    ];

    if (!$responseNode['running']) {
        $statusIcon = $statusRedCircle;
        $errors[] = "RabbitMQ is reporting that node {$responseNode['name']} is not running correctly, known VPN issue on server.";
    }
}

$queues = [];
foreach ($responseQueues as $responseQueue) {
    $queues[] = [
        'name' => $responseQueue['name'],
        'state' => $responseQueue['state'],
    ];

    if ($responseQueue['state'] !== "running") {
        $statusIcon = $statusRedCircle;
        $errors[] = "RabbitMQ is reporting that queue {$responseQueue['name']} is not running correctly.";
    }
}


if ($queuedMessageCountErrorLimit && intval($messagesReady) > $queuedMessageCountErrorLimit) {
    $statusIcon = $statusRedCircle;
    $errors[] = "Number of message in queue higher than $queuedMessageCountErrorLimit, please check consumers status.";
}

// Top menu : status icon + count message ready
echo $statusIcon . " " . $messagesReady . "|color=$colorBlack\n";
echo "---\n";
// Error messages
foreach ($errors as $error) {
    echo "$error | color=$colorRed\n";
}
if ($errors) {
    echo "---\n";
}
// Message count detail
echo "Queued messages\n";
echo "Ready: $messagesReady | color=$colorBlack\n";
echo "Unacked: $messagesUnacked | color=$colorBlack\n";
echo "Total: $messagesTotal | color=$colorBlack\n";
echo "---\n";
// Global count for Connections, Channels, Exchanges, Queues, Consumers:
echo "Global counts\n";
echo "Connections: $connections | color=$colorBlack\n";
echo "Channels: $channels | color=$colorBlack\n";
echo "Exchanges: $exchanges | color=$colorBlack\n";
echo "Queues: $queuesCount | color=$colorBlack\n";
echo "Consumers: $consumers | color=$colorBlack\n";
echo "---\n";
// Nodes status
echo "Nodes\n";
foreach ($nodes as $node) {
    $nodeStatus = $node['running'] ? "ok" : "error";
    $nodeStatusColor = $node['running'] ? $colorBlack : $colorRed;
    echo "{$node['name']} status $nodeStatus | color=$nodeStatusColor\n";
}
echo "---\n";
echo "Queues\n";
foreach ($queues as $queue) {
    $queueStatusColor = $queue['state'] === "running" ? $colorBlack : $colorRed;
    echo "{$queue['name']} status {$queue['state']} | color=$queueStatusColor\n";
}
echo "---\n";
// manual refresh + RabbitMQ version TAG + management interface link
echo "RabbitMQ v$rabbitMqVersion, Erlang $erlangVersion\n";
echo "$baseUrl:$port | href=$baseUrl:$port \n";
echo "Refresh | refresh=true \n";

curl_close($ch);

#!/usr/bin/env /usr/local/bin/node

// <bitbar.title>Semaphore CI</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Goran Gajic</bitbar.author>
// <bitbar.author.github>gorangajic</bitbar.author.github>
// <bitbar.desc>List your project and their statuses, from semaphoreci.com</bitbar.desc>
// <bitbar.dependencies>node.js</bitbar.dependencies>
// <bitbar.image>http://i.imgur.com/tRd1clI.png</bitbar.image>

var https = require('https');
var AUTH_TOKEN = ''; // YOUR AUTH TOKEN
var url = 'https://semaphoreci.com/api/v1/projects?auth_token='+AUTH_TOKEN;
var icon = '✅';

function status(result) {
    if (result === 'passed') {
        return '✅';
    }

    if (result === 'pending') {
        return '🌀';
    }
    return '🔴';
}

function color(result) {
    if (result === 'passed') {
        return 'green';
    }
    if (result === 'pending') {
        return 'orange';
    }

    return 'red';
}

function branchesOutput(branches) {
    return branches.map(function (branch){
        if (branch.result === 'failed') {
            icon = '🔴';
        }
        return ['- ', branch.branch_name,' ',status(branch.result), ' | color=', color(branch.result), ' href=', branch.build_url].join('');
    }).join('\n');
}

function handleResponse(body) {
    var output = body.map(function(project){
        return [project.owner, '/', project.name, ' | href=', project.html_url,'\n', branchesOutput(project.branches)].join('');
    }).join('\n---\n');
    console.log(icon + '\n---\n ⚙ Semaphoreci | href=https://semaphoreci.com/ \n---\n' + output);
}

https.get(url, function(res) {
    var body = '';
    res.on('data', function(data) {
        body += data;
    });
    res.on('end', function() {
        handleResponse(JSON.parse(body));
    });
});

#!/usr/bin/env /usr/local/bin/node

// <bitbar.title>Sentry</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Tommy Brunn</bitbar.author>
// <bitbar.author.github>nevon</bitbar.author.github>
// <bitbar.desc>Shows your most recent error reports from Sentry (https://getsentry.com)</bitbar.desc>
// <bitbar.dependencies>node.js</bitbar.dependencies>
// <bitbar.image>http://i.imgur.com/GdEXQfY.png</bitbar.image>

// jshint asi:true
var https = require('https')

/* EDIT HERE */
var AUTH_TOKEN = ''
var ORGANIZATION = ''
var PROJECT = ''
var ISSUE_COUNT = 5
/* DON'T EDIT BELOW */

var API_URL = 'https://' + AUTH_TOKEN + ':@app.getsentry.com/api/0/'
var PROJECT_URL = 'https://app.getsentry.com/' + ORGANIZATION + '/' + PROJECT
var TITLE = [ORGANIZATION + '/' + PROJECT, '@', 'Sentry'].join(' ')

function statusColor (issue) {
  var status = issue.status
  var isAssigned = issue.assignedTo !== null
  if (status === 'resolved' || status === 'muted') {
    return 'green'
  }
  if (status === 'unresolved' && isAssigned) {
    return 'orange'
  }

  return 'red'
}

function timeSince (date) {
  if (typeof date !== 'object') {
    date = new Date(date)
  }

  var seconds = Math.floor((new Date() - date) / 1000)
  var intervalType

  var interval = Math.floor(seconds / 31536000)
  if (interval >= 1) {
    intervalType = 'year'
  } else {
    interval = Math.floor(seconds / 2592000)
    if (interval >= 1) {
      intervalType = 'month'
    } else {
      interval = Math.floor(seconds / 86400)
      if (interval >= 1) {
        intervalType = 'day'
      } else {
        interval = Math.floor(seconds / 3600)
        if (interval >= 1) {
          intervalType = 'hour'
        } else {
          interval = Math.floor(seconds / 60)
          if (interval >= 1) {
            intervalType = 'minute'
          } else {
            interval = seconds
            intervalType = 'second'
          }
        }
      }
    }
  }

  if (interval > 1 || interval === 0) {
    intervalType += 's'
  }

  return interval + ' ' + intervalType
}

function trimString (str, n) {
  n = n || 72
  return (str.length > n) ? str.substr(0, n - 1) + '…' : str
}

function formatTitle (issue) {
  return [trimString(issue.title), ' | href=', issue.permalink, ' size=11', ' color=' + statusColor(issue)].join('')
}

function formatCulprit (issue) {
  return [trimString(issue.culprit), '| size=10'].join('')
}

function formatCount (count) {
  var str = (count === 1) ? 'occurrence' : 'occurrences'
  return [count, str, '| size=10'].join(' ')
}

function formatTimes (issue) {
  var lastSeen = timeSince(new Date(issue.lastSeen)) + ' ago'
  var firstSeen = timeSince(new Date(issue.firstSeen)) + ' old'
  return [lastSeen, '-', firstSeen, '| size=10'].join(' ')
}

function formatIssue (issue) {
  return [
    formatTitle(issue),
    formatCulprit(issue),
    formatTimes(issue),
    formatCount(issue.count)
  ].join('\n')
}

function handleResponse (body) {
  var output = body.map(formatIssue).join('\n---\n')
  console.log('Sentry' + '\n---\n' + TITLE + ' | href=' + PROJECT_URL + '\n---\n' + output)
}

https.get(API_URL + 'projects/' + ORGANIZATION + '/' + PROJECT + '/issues/?query=is%3Aunresolved&limit=' + ISSUE_COUNT + '&sort=date&statsPeriod=24h', function (res) {
  var body = ''
  res.on('data', function (data) {
    body += data
  })
  res.on('end', function () {
    handleResponse(JSON.parse(body))
  })
})

#!/usr/bin/swift

// # <bitbar.title>Trending Swift on GitHub</bitbar.title>
// # <bitbar.version>v1.0</bitbar.version>
// # <bitbar.author>Reda Lemeden</bitbar.author>
// # <bitbar.author.github>kaishin</bitbar.author.github>
// # <bitbar.desc>List Swift repositories tending on GitHub in a given period.</bitbar.desc>
// # <bitbar.image>https://github.com/kaishin/swift-trending-bitbar/raw/master/screenshot.png</bitbar.image>
// # <bitbar.dependencies>swift</bitbar.dependencies>
// # <bitbar.abouturl>https://github.com/kaishin/swift-trending-bitbar</bitbar.abouturl>

import Foundation

// PREFERENCES (Feel free to change these to your liking)

var displayCount = 15 // Min: 10, Max: 25
var maxSubtitleLineLength = 70
var trendingPeriod = "daily" // Possible values: "daily", "weekly", "monthly"

// Try not to edit the code below.

let icon = ""
let templateImage = "|templateImage=\(icon)"

// Mark: - Regex

infix operator =~

func =~ (value: String, pattern: String) -> RegexResult {
  var error: NSError?
  let string = value as NSString
  let options = NSRegularExpression.Options(rawValue: 0)
  let regex: NSRegularExpression?

  do {
    regex = try NSRegularExpression(pattern: pattern, options: options)
  } catch let error1 as NSError {
    error = error1
    regex = nil
  }

  if error != nil { return RegexResult(results: []) }

  let all = NSRange(location: 0, length: string.length)
  let matchingOptions = NSRegularExpression.MatchingOptions(rawValue: 0)
  var matches: [String] = []

  regex?.enumerateMatches(in: value, options: matchingOptions, range: all) { result, _, _ in
    guard let result = result else { return }
    let subString = string.substring(with: result.range)
    matches.append(subString)
  }

  return RegexResult(results: matches)
}

struct RegexResult {
  let isMatching: Bool
  let matches: [String]

  init(results: [String]) {
    matches = results
    isMatching = matches.count > 0
  }
}

// Mark: - Foundation Extensions

extension String {
  func matches(pattern: String) -> [String] {
    let regexResult = (self =~ pattern)

    if regexResult.isMatching {
      return regexResult.matches
    } else {
      return []
    }
  }

  func condenseWhitespace() -> String {
    let components = self.components(separatedBy: NSCharacterSet.whitespacesAndNewlines)
    return components.filter { !$0.isEmpty }.joined(separator: " ")
  }

  func trunc(length: Int, trailing: String = "…") -> String {
    return (self.count > length) ? self.prefix(length) + trailing : self
  }
}

extension Array {
  func chunk(_ chunkSize: Int) -> [[Element]] {
    return stride(from: 0, to: self.count, by: chunkSize).map({ (startIndex) -> [Element] in
      let endIndex = (startIndex.advanced(by: chunkSize) > self.count) ? self.count-startIndex : chunkSize
      return Array(self[startIndex..<startIndex.advanced(by: endIndex)])
    })
  }
}

// Mark: - Repository

struct Repository {
  let authorName: String
  let projectName: String
  let description: String
  let starCount: Int
  let newStarCount: Int

  init?(string: String) {
    let properties = string.split(separator: "|")
    if properties.count != 4 { return nil }

    self.authorName = String(describing: properties[0].split(separator: "/").first ?? "").trimmingCharacters(in: .whitespacesAndNewlines)
    self.projectName = String(describing: properties[0].split(separator: "/").last ?? "").trimmingCharacters(in: .whitespacesAndNewlines)
    self.description = String(describing: properties[1])
      .trimmingCharacters(in: .whitespacesAndNewlines)
      .replacingOccurrences(of: "&amp;", with: "&")

    let stars = String(describing: properties[2]
      .split(separator: " ").first?.replacingOccurrences(of: ",", with: "") ?? "0")
    self.starCount = Int(stars) ?? 0

    let newStarsString = String(describing: properties[3]
      .split(separator: " ").first?.replacingOccurrences(of: ",", with: "") ?? "0")

    self.newStarCount = Int(newStarsString) ?? 0
  }

  var gitHubURL: String {
    return "https://github.com/\(authorName)/\(projectName)/"
  }

  var firstLine: String {
    return "\(projectName) (by \(authorName))" + "| href=\(gitHubURL)"
  }

  var secondLine: String {
    return "★\(starCount) (+\(newStarCount)) — \(description)" + "| size=12 length=\(maxSubtitleLineLength)"
  }

  func multiLineDescription(wordCount count: Int) -> String {
    let fullDescriptionWords = description.split(separator: " ")
    let chunkedDescription = fullDescriptionWords.chunk(count).map { chunk in
      return chunk.joined(separator: " ")
    }

    return  chunkedDescription.joined(separator: "| size=12 \n")
  }
}

enum Period: String {
  case daily, weekly, monthly

  var title: String {
    switch self {
    case .daily:
      return "today"
    case .weekly:
      return "this week"
    default:
      return "this month"
    }
  }
}

// Mark: - Free Functions

func trendingRepositories(html: String) -> [Repository] {
  let repos = html.matches(pattern: "<ol class=\"repo-list\">(.|\n)*?</ol>")[0]
  let repoList = repos.matches(pattern: "<li class=(.|\n)*?</li>")

  return repoList.flatMap { repo in
    let sanitizedString = repo.replacingOccurrences(of: "<[^>]+>", with: "", options: .regularExpression)
      .replacingOccurrences(of: "\n    Star|Built by\n|\n          Swift", with: "|", options: .regularExpression)
      .condenseWhitespace()

    return Repository(string: sanitizedString)
  }
}

func printOutput(responseHTML html: String) {
  print(templateImage)
  print("---")

  guard let period = Period(rawValue: trendingPeriod) else {
    fatalError("Frequency specified should be one of the following options: 'daily', 'weekly', 'monthly'.")
  }

  print("Trending Swift \(period.title.capitalized)")
  print("---")

  let count = max(min(displayCount, 25), 10)

  for repo in trendingRepositories(html: html)[0..<count] {
    print(repo.firstLine)
    print(repo.secondLine)
  }
}

// Mark: - Output

let url = URL(string: "https://github.com/trending/swift?since=\(trendingPeriod)")!
let html = try? String(contentsOf: url)

printOutput(responseHTML: html!)

#!/usr/bin/env /usr/local/bin/node

// <bitbar.title>VSO Pullrequests</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Jelle Kralt</bitbar.author>
// <bitbar.author.github>jellekralt</bitbar.author.github>
// <bitbar.desc>Lists open pull requests from VSO</bitbar.desc>
// <bitbar.dependencies>node</bitbar.dependencies>
// <bitbar.image>http://i.imgur.com/b3KTgcg.png</bitbar.image>

var https = require('https');

var USERNAME = 'vso_username';
var PASSWORD = 'vso_password';
var DOMAIN = 'vso_domain';
var COLLECTION = 'vso_collection';
var REPO = 'repository_id';

/**
 * Makes an HTTP GET call
 * @param {string} path
 */
function get(path) {
    // Use native promise in favor of library so there is no dependency
    return new Promise( function (resolve, reject) {
        var options = {
            host: DOMAIN,
            path: path,
            auth: USERNAME + ':' + PASSWORD,
            json: true
        };

        return https.get(options, function (res) {
            var body = '';
            res.on('data', function (data) {
                body += data;
            });
            res.on('end', function () {
                resolve(JSON.parse(body));
            });
            res.on('error', function (err) {
                reject(err);
            });
        });
    });

}

/**
 * Handles API calls response
 * @param {Object} repo - Repository data
 * @param {Object} pullRequests - Pull requests data
 */
function handleResponse(repo, pullRequests) {
    console.log('↓⤸ ' + pullRequests.length + ' | dropdown=false');
    console.log('---');
    pullRequests.forEach(function(pr) {
        console.log(pr.pullRequestId + ': ' + pr.title + ' | href=' + repo._links.web.href + '/pullrequest/' + pr.pullRequestId);
    });
    console.log('---');
    console.log('✚ Create PR | href=' + repo._links.web.href + '/pullrequests?_a=createnew');
}

// Use native promise in favor of library so there is no dependency
Promise.all([
    get('/' + COLLECTION + '/_apis/git/repositories/' + REPO),
    get('/' + COLLECTION + '/_apis/git/repositories/' + REPO + '/pullrequests')
]).then(function(data) {
    handleResponse(data[0], data[1].value);
});

#!/usr/bin/perl
# <bitbar.title>Vagrant</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>
# <bitbar.author.github>axeloz</bitbar.author.github>
# <bitbar.author>Axel</bitbar.author>
# <bitbar.desc>Vagrant status checker.</bitbar.desc>
# <bitbar.dependencies>perl,vagrant</bitbar.dependencies>
# <bitbar.image>https://i.imgur.com/Yzrcz9k.png</bitbar.image>

use strict;
use Cwd 'abs_path';

my @output;
my @found;
my $machinePath;
my $readablePath;
my $me = abs_path($0);
my $content;
my $vagrant;
my $running = 0;
my $total = 0;

# HACK as $PATH is incorrect when Bitbar run the script
# Must add /usr/local/bin manually
my $path = $ENV{PATH}.':/usr/local/bin';

# This function allows me to run Apple Scripts
sub osascript($) { system 'osascript', map { ('-e', $_) } split(/\n/, $_[0]); }

# Locating the Vagrant binary
foreach $a (split(/:/, $path)) {
	if (-x $a."/vagrant") {
		$vagrant = $a."/vagrant";
		last;
	}
}

# If Vagrant could not be found
if (! defined $vagrant) {
	print "⚠️\n";
	print "---\n";
	print "Vagrant binary could not be found in paths. Is it installed?";
	exit 1;
}

# When script is called with arguments
# $ARGV[0] : the action (up, halt, suspend, resume, ssh)
# $ARGV[1] : the path of the Vagrant environment
# $ARGV[2] : the ID of the VM
if ( ($#ARGV + 1) == 3) {

	my $title = "Vagrant machine #$ARGV[2]";
	my $description = "";
	my $newstatus = "unknown";

	# Running the SSH action
	if ($ARGV[0] eq 'ssh') {
		$ARGV[1] =~ s/\\/\\\\/g; # extra escape; escape from osascript and do script
		&osascript ('
			tell application "Terminal"
				if (count of windows) is 0 then reopen
			  activate
			  do script "cd '.$ARGV[1].' && vagrant ssh"
			end tell
		');
		$description = "You are now connected to your Vagrant machine";
	}
	else {
		if ($ARGV[0] eq "up" || $ARGV[0] eq "resume") {
			$newstatus = "running";
		}
		elsif  ($ARGV[0] eq "halt" || $ARGV[0] eq "suspend") {
			$newstatus = "stopped";
		}
		elsif ($ARGV[0] eq "reload") {
			$newstatus = "reloaded";
		}

		system("export PATH=$path && cd $ARGV[1] && $vagrant $ARGV[0]");
		$description = "Vagrant virtual machine status is now ".$newstatus;
	}

	# Checking the result of the action
	if ($? eq 0) {
		&osascript (
		 'display notification "'.$description.'" with title "'.$title.'"'
		);
		exit 0;
	}
	else {
		&osascript (
		 'display notification "Could not execute operation" with title "'.$title.'"'
		);
		exit 1;
	}
	# Not needed, just safer...
	exit 0
}

# Getting the list of all Vagrant VMs
@output = `$vagrant global-status |tail -n +3`;

# Checking whether there is at least one VM
# TODO: clean this
foreach $a (@output) {
	if ($a =~ "There are no active") {
		print "⚠️\n";
		print "---\n";
		print "There is no Vagrant VM yet.";
		exit 1;		
	}
}


# Looping in the list
foreach $a (@output) {
	# Triming spaces
	$a =~ s/^\s+|\s+$//g;
	# Removing excessive spaces
	$a =~ s/ {1,}/ /g;

	# Cutting output on first empty line as Vagrant is too verbose
	last if ($a eq '');

	# Counting total
	$total ++;

	# Exploding row on spaces
	@found = split / /, $a;

	$machinePath  = join("\\ ", @found[4..$#found]);
	$readablePath = join(" ", @found[4..$#found]);

	# This VM is currently running
	if ($found[3] eq 'running') {
		# Counting the running VMs
		$running ++;

		$content .= "✅ Machine #$found[0] is running | size=14 color=green\n";
		$content .= " $readablePath | size=11 \n";
		$content .= "  | size=14 color=black \n";

		$content .= "#️⃣ SSH $found[0] | size=12 bash=\"$me\" param1=ssh param2=\"".$machinePath."\" param3=\"".$found[0]."\" terminal=false refresh=false \n";
		$content .= "🔄 Reload $found[0] | size=12 bash=\"$me\" param1=reload param2=\"".$machinePath."\" param3=\"".$found[0]."\" terminal=false refresh=true \n";
		$content .= "🔽 Suspend $found[0] | size=12 bash=\"$me\" param1=suspend param2=\"".$machinePath."\" param3=\"".$found[0]."\" terminal=false refresh=true \n";
		$content .= "⏬ Stop $found[0] | size=12 bash=\"$me\" param1=halt param2=\"".$machinePath."\" param3=\"".$found[0]."\" terminal=false refresh=true \n";
	}
	# This VM is currently saved
	elsif ($found[3] eq 'saved' || $found[3] eq 'suspended') {
		$content .= "📴 Machine #$found[0] is suspended | size=14 color=orange\n";
		$content .= " $readablePath | size=11 \n";
		$content .= "  | size=14 color=black \n";
		$content .= "▶️ Resume $found[0] | size=12 bash=\"$me\" param1=resume param2=\"".$machinePath."\" param3=\"".$found[0]."\" terminal=false refresh=true \n";
		$content .= "⏬ Stop $found[0] | size=12 bash=\"$me\" param1=halt param2=\"".$machinePath."\" param3=\"".$found[0]."\" terminal=false refresh=true \n";
	}
	# This VM is currently powered off
	elsif ($found[3] eq 'poweroff' || $found[3] eq 'aborted' || ($found[3] eq 'not' && $found[4] eq "running")) {
		if ($found[3] eq 'not' && $found[4] eq "running") {
			$machinePath  = join("\\ ", @found[5..$#found]);
			$readablePath = join(" ", @found[5..$#found]);
		}
		$content .= "🚫 Machine #$found[0] is stopped | size=14 color=red\n";
		$content .= " $readablePath | size=11 \n";
		$content .= "  | size=14 color=black \n";
		$content .= "▶️ Start $found[0] | size=12 bash=\"$me\" param1=up param2=\"".$machinePath."\" param3=\"".$found[0]."\" terminal=false refresh=true \n";
	}
	# This VM is in an unknown state
	else {
		$content .= "❓ Machine #$found[0] is ".$found[3]." | size=14 color=red\n";
		$content .= " $machinePath | size=11 \n";
		$content .= "  | size=14 color=black \n";
		$content .= "This is an unknown state\n";
	}

	# Adding the terminal separator
	$content .= "---\n";
}

# Adding the menu title with the number of running VMs
print "V [$running/$total]\n";
print "---\n";
print $content unless !defined $content;
exit 0;

#!/usr/bin/env perl
# <bitbar.title>Vagrant Global Status</bitbar.title>
# <bitbar.version>1.1</bitbar.version>
# <bitbar.author>Alexandre Espinosa Menor</bitbar.author>
# <bitbar.author.github>alexandregz</bitbar.author.github>
# <bitbar.desc>Show vagrant images running, from vagrant global-status command</bitbar.desc>
# <bitbar.image>http://i.imgur.com/4YAFZC6.png</bitbar.image>
#
# "running" line run command "vagrant suspend"
# "saved, poweroff, aborted" line run command "vagrant up"
#

use strict;

$ENV{'PATH'} = $ENV{'PATH'}.':/usr/local/bin'; 

if ($#ARGV >= 1) {
        exit exec_sub_command(@ARGV);
}

# action => [status1 from machine, status2, ...]
my $actions_from_status = {
        up => ["saved", "poweroff", "aborted"],
        suspend => ["running"],
        resume => ["suspended"]
};

my $status = `vagrant global-status`;

if($status =~ /^\-{10,}\n(.*)\n\s+\n/sm) {
        my @images = split(/\n/, $1);

        print "🇻"; print "(".scalar(@images).")" if(@images);
        print "\n";
        print "---\n";

        foreach my $i(@images) {
                my @data_image = split(/\s+/, $i);
                my $i_id = $data_image[0];
                my $i_image = $data_image[1];
                my $i_provider = $data_image[2];
                my $i_status = $data_image[3];
                my $i_path = $data_image[4];

                my $color = "black";
                $color = "green" if($i_status eq 'running');
                $color = "red" if($i_status eq 'saved');

                print "$i_id - $i_image ($i_provider) | color=black\n";
                print "   $i_path | bash=$0 param1=path_copy param2=$i_path color=gray trim=false terminal=false\n";

                print "$i_status | color=$color ";
                foreach my $action(keys(%{$actions_from_status})) {
                        print "bash=vagrant param1=$action param2=$i_id terminal=true" if(grep $_ eq $i_status, @{$actions_from_status->{$action}});
                }
                print "\n";
                print "---\n";
        }
}
else{
        #print "Without Vagrant images running?";
        print "🇻(0)";
}

sub exec_sub_command {
        my ($sub_command, @args) = @_;
        my $sub_command_methods = {
                'path_copy' => \&sub_command_path_copy,
        };
        if (!defined $sub_command_methods->{$sub_command}) {
                die "Undefined sub command: $sub_command";
        }
        return $sub_command_methods->{$sub_command}(@args);
}

sub sub_command_path_copy {
        my $path = shift;
        open my $fh, '|/usr/bin/pbcopy' or die $!;
        print $fh $path;
        close $fh;
        return;
}

#!/usr/bin/env /usr/local/bin/node

/*
  <bitbar.title>Vigil Website Monitoring</bitbar.title>
  <bitbar.version>v1.0</bitbar.version>
  <bitbar.author>Cameron Rye</bitbar.author>
  <bitbar.author.github>cameronrye</bitbar.author.github>
  <bitbar.desc>Displays the status of hosts being monitered by Vigil.</bitbar.desc>
  <bitbar.image>https://cameronrye.com/img/vigil-check.png</bitbar.image>
  <bitbar.dependencies>node</bitbar.dependencies>

  Vigil Website Monitoring
  by Cameron Rye (@cameronrye)

  Auth token can be scraped from the status board url @ http://client.vigil-app.com/?p=integrations&t=sb

*/

'use strict';

var https = require('https'),
    AUTH_TOKEN = 'YOUR AUTH TOKEN',
    url = 'https://api.vigil-app.com/v1/user/' + AUTH_TOKEN + '/host?populateHostMonitors';

function createBar(data) {
  var isDown = false,
      hosts = '',
      bitbar = '';

  hosts = data.hosts.map(function(host) {
    var vigil = '',
        protocol = '',
        method = '',
        time = '',
        params = '';

    if (!host.hostMonitors[0].lastMonitorResult.resultCode) isDown = true;

    time = new Date(host.hostMonitors[0].lastMonitorResult.endTimestamp);

    params = host.hostMonitors[0].parameters.map(function(param) {
      switch (param.monitorParameterName) {
        case 'monitors.webpage.protocol':
          protocol = (param.value === 'https') ? 'https://' : 'http://';
          break;
        case 'monitors.webpage.method':
          method = param.value;
          break;
      }
    });

    vigil  = host.hostMonitors[0].lastMonitorResult.resultCode ? ' ▲ ':' ▼ ';
    vigil += host.hostMonitors[0].name + '| href=' + protocol + host.hostName + '\n';
    vigil += method + '\u00A0\u00A0\u00A0' + protocol + host.hostName + '/\n';
    vigil += 'Status Code: ' + host.hostMonitors[0].lastMonitorResult.resultProperties['monitors.webpage.statusCode'];
    vigil += ' (' + getStatus(host.hostMonitors[0].lastMonitorResult.resultProperties['monitors.webpage.statusCode']) + ')\n';
    vigil += 'Response Time: ' + (host.hostMonitors[0].lastMonitorResult.resultProperties['monitors.webpage.stats.totalSeconds'] * 1000) + 'ms\n';
    vigil += 'Avg Response: ' + (host.hostMonitors[0].uptime.responseTimeMean * 1000) + 'ms\n';
    vigil += 'Uptime: ' + host.hostMonitors[0].uptime.uptimePercent + '%\n';
    vigil += time + '| size: 10';

    return vigil;

  }).join('\n---\n');

  bitbar  = isDown ? '▼':'▲';
  bitbar += '| dropdown=false\n---\n';
  bitbar += ' ▬\n---\n';
  bitbar += hosts;
  bitbar += '\n---\n ⚙ Vigil Website Monitoring | href=https://client.vigil-app.com/ \n---\n';

  console.log(bitbar);
}

function getStatus(code) {
  var status = '';
  switch (code) {
    case '100':
      status = 'Continue';
      break;
    case '101':
      status = 'Switching Protocol';
      break;
    case '200':
      status = 'OK';
      break;
    case '201':
      status = 'Created';
      break;
    case '202':
      status = 'Accepted';
      break;
    case '203':
      status = 'Non-Authoritative Information';
      break;
    case '204':
      status = 'No Content';
      break;
    case '205':
      status = 'Reset Content';
      break;
    case '206':
      status = 'Partial Content';
      break;
    case '300':
      status = 'Multiple Choice';
      break;
    case '301':
      status = 'Moved Permanently';
      break;
    case '302':
      status = 'Found';
      break;
    case '303':
      status = 'See Other';
      break;
    case '304':
      status = 'Not Modified';
      break;
    case '305':
      status = 'Use Proxy';
      break;
    case '306':
      status = 'Unused';
      break;
    case '307':
      status = 'Temporary Redirect';
      break;
    case '308':
      status = 'Permanent Redirect';
      break;
    case '400':
      status = 'Bad Request';
      break;
    case '401':
      status = 'Unauthorized';
      break;
    case '402':
      status = 'Payment Required';
      break;
    case '403':
      status = 'Forbidden';
      break;
    case '404':
      status = 'Not Found';
      break;
    case '405':
      status = 'Method Not Allowed';
      break;
    case '406':
      status = 'Not Acceptable';
      break;
    case '407':
      status = 'Proxy Authentication Required';
      break;
    case '408':
      status = 'Request Timeout';
      break;
    case '409':
      status = 'Conflict';
      break;
    case '410':
      status = 'Gone';
      break;
    case '411':
      status = 'Length Required';
      break;
    case '412':
      status = 'Precondition Failed';
      break;
    case '413':
      status = 'Payload Too Large';
      break;
    case '414':
      status = 'URI Too Long';
      break;
    case '415':
      status = 'Unsupported Media Type';
      break;
    case '416':
      status = 'Requested Range Not Satisfiable';
      break;
    case '417':
      status = 'Expectation Failed';
      break;
    case '418':
      status = 'I\'m a teapot';
      break;
    case '421':
      status = 'Misdirected Request';
      break;
    case '426':
      status = 'Upgrade Required';
      break;
    case '428':
      status = 'Precondition Required';
      break;
    case '429':
      status = 'Too Many Requests';
      break;
    case '431':
      status = 'Request Header Fields Too Large';
      break;
    case '500':
      status = 'Internal Server Error';
      break;
    case '501':
      status = 'Not Implemented';
      break;
    case '502':
      status = 'Bad Gateway';
      break;
    case '503':
      status = 'Service Unavailable';
      break;
    case '504':
      status = 'Gateway Timeout';
      break;
    case '505':
      status = 'HTTP Version Not Supported';
      break;
    case '506':
      status = 'Variant Also Negotiates';
      break;
    case '507':
      status = 'Variant Also Negotiates';
      break;
    case '511':
      status = 'Network Authentication Required';
      break;
  }
  return status;
}

https.get(url, function(res) {
