#!/usr/local/bin/node

// <bitbar.title>Moon Phase</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Volker Wieban</bitbar.author>
// <bitbar.author.github>hpcodecraft</bitbar.author.github>
// <bitbar.desc>Shows the current moon phase</bitbar.desc>
// <bitbar.image>https://cloud.githubusercontent.com/assets/1476865/24201253/ce0d8c5e-0f0f-11e7-8e44-503654407850.png</bitbar.image>
// <bitbar.dependencies>node</bitbar.dependencies>

// Moon phase calculations taken from https://github.com/tingletech/moon-phase
Date.prototype.getJulian = function() {
    return ((this / 86400000) - (this.getTimezoneOffset() / 1440) + 2440587.5);
};

function moon_day(today) {
    var GetFrac = function(fr) {
        return (fr - Math.floor(fr));
    };
    var thisJD = today.getJulian();
    var year = today.getFullYear();
    var degToRad = 3.14159265 / 180;
    var K0, T, T2, T3, J0, F0, M0, M1, B1, oldJ;
    K0 = Math.floor((year - 1900) * 12.3685);
    T = (year - 1899.5) / 100;
    T2 = T * T;
    T3 = T * T * T;
    J0 = 2415020 + 29 * K0;
    F0 = 0.0001178 * T2 - 0.000000155 * T3 + (0.75933 + 0.53058868 * K0) - (0.000837 * T + 0.000335 * T2);
    M0 = 360 * (GetFrac(K0 * 0.08084821133)) + 359.2242 - 0.0000333 * T2 - 0.00000347 * T3;
    M1 = 360 * (GetFrac(K0 * 0.07171366128)) + 306.0253 + 0.0107306 * T2 + 0.00001236 * T3;
    B1 = 360 * (GetFrac(K0 * 0.08519585128)) + 21.2964 - (0.0016528 * T2) - (0.00000239 * T3);
    var phase = 0;
    var jday = 0;
    while (jday < thisJD) {
        var F = F0 + 1.530588 * phase;
        var M5 = (M0 + phase * 29.10535608) * degToRad;
        var M6 = (M1 + phase * 385.81691806) * degToRad;
        var B6 = (B1 + phase * 390.67050646) * degToRad;
        F -= 0.4068 * Math.sin(M6) + (0.1734 - 0.000393 * T) * Math.sin(M5);
        F += 0.0161 * Math.sin(2 * M6) + 0.0104 * Math.sin(2 * B6);
        F -= 0.0074 * Math.sin(M5 - M6) - 0.0051 * Math.sin(M5 + M6);
        F += 0.0021 * Math.sin(2 * M5) + 0.0010 * Math.sin(2 * B6 - M6);
        F += 0.5 / 1440;
        oldJ = jday;
        jday = J0 + 28 * phase + Math.floor(F);
        phase++;
    }

    // 29.53059 days per lunar month
    return (((thisJD - oldJ) / 29.53059));
}

function phase_text(phase) {
    var txt_phase;
    if (phase <= 0.0625 || phase > 0.9375) {
        txt_phase = "new_moon";
    } else if (phase <= 0.1875) {
        txt_phase = "waxing_crescent_moon";
    } else if (phase <= 0.3125) {
        txt_phase = "first_quarter_moon";
    } else if (phase <= 0.4375) {
        txt_phase = "waxing_gibbous_moon";
    } else if (phase <= 0.5625) {
        txt_phase = "full_moon";
    } else if (phase <= 0.6875) {
        txt_phase = "waning_gibbous_moon";
    } else if (phase <= 0.8125) {
        txt_phase = "last_quarter_moon";
    } else if (phase <= 0.9375) {
        txt_phase = "waning_crescent_moon";
    }

    return txt_phase;
}

var phaseLabel = {
  "new_moon"              : "New moon",
  "waxing_crescent_moon"  : "Waxing crescent moon",
  "first_quarter_moon"    : "First quarter moon",
  "waxing_gibbous_moon"   : "Waxing gibbous moon",
  "full_moon"             : "Full moon",
  "waning_gibbous_moon"   : "Waning gibbous moon",
  "last_quarter_moon"     : "Third quarter moon",
  "waning_crescent_moon"  : "Waning crescent moon",
};

var phase     = moon_day(new Date()),
    phase_str = phase_text(phase),
    output    = ':' + phase_str + ':|dropdown=false \n' +
              '---\n' +
              phaseLabel[phase_str] + '\n' +
              "Completed " + Math.round(phase*100) + "% of lunar cycle";

console.log(output);

#!/usr/bin/env perl

# Inspired from [Simple Todo Tracker](https://getbitbar.com/plugins/Lifestyle/todo.30s.sh)

use strict;
use warnings;

# Edit the path to the file describe your ToDo
my $file_path = "$ENV{HOME}/Dropbox/TODO.md";

# Select your editor
my $editor = "/Applications/Atom.app/Contents/Resources/app/atom.sh"; # Atom
# my $editor = "/Applications/TextMate.app/Contents/Resources/mate"; # TextMate
# my $editor = "/Applications/Sublime Text.app/Contents/SharedSupport/bin/subl"; # Sublime Text
# my $editor = "/Applications/CotEditor.app/Contents/SharedSupport/bin/cot"; # CotEditor
# my $editor = "/Applications/Emacs.app/Contents/MacOS/Emacs"; # Emacs

# Change here depending on your preference
my $font_color_finished = "#C0C0C0";
my $font_color_unfinieshed = "black";
my $font_color_label = "gray";
my $menu_bar_icon = ":ballot_box_with_check:";

# <bitbar.title>Simple ToDo Tracker for Markdown</bitbar.title>
# <bitbar.version>v1.1</bitbar.version>
# <bitbar.author>Kenji Akiyama</bitbar.author>
# <bitbar.author.github>artifactsauce</bitbar.author.github>
# <bitbar.desc>Tracking ToDo list described in Markdown</bitbar.desc>
# <bitbar.image>http://i.imgur.com/nIEVIdR.png</bitbar.image>
# <bitbar.dependencies>perl</bitbar.dependencies>

my $unfinished_task_cnt = 0;
my @tasks = ();

open my $fh, "<", $file_path or die $!;
my $content = do { local $/; <$fh> };
close $fh;

$content =~s/<!--.*?-->//sg;

for ( split /\n/, $content ) {
    next unless $_ =~/^\s*(?:[\-\+\*]|\d+\.?)\s{1,3}\[(.)\]\s+(.+)$/;
    my $task = {
        checked => ($1 eq "x"),
        title => $2,
    };

    if ( $task->{title} =~s/^(?:label\:(\S+))?\s*(.+)$/$2/i ) {
        $task->{label} = $1;
    }

    if ( $task->{title} =~s/\[(.+?)\]\((\S+?)\)/$1/ ) {
        $task->{url} = $2;
    }

    $unfinished_task_cnt++ unless $task->{checked};
    push @tasks, $task;
}

print "$menu_bar_icon:$unfinished_task_cnt\n";
print "---\n";

for ( my $i = 0; $i <= $#tasks; $i++ ) {
    my $font_color = $tasks[$i]->{checked} ? $font_color_finished : $font_color_unfinieshed;
    my $option_string = "color=$font_color trim=false";
    $option_string .= " href=$tasks[$i]->{url}" if defined $tasks[$i]->{url};

    if ( ! defined $tasks[$i]->{label} ) {
        printf "%s | %s\n", $tasks[$i]->{title}, $option_string;
    }
    elsif ( defined $tasks[$i-1]->{label} && $tasks[$i-1]->{label} eq $tasks[$i]->{label}) {
        printf "    %s | %s\n", $tasks[$i]->{title}, $option_string;
    }
    else {
        printf "%s | color=$font_color_label\n", $tasks[$i]->{label};
        printf "    %s | %s\n", $tasks[$i]->{title}, $option_string;
    }
}

print "---\n";
print "Open file | bash=$editor param1=$file_path terminal=false\n";
# print "Refresh | refresh=true\n"; # enable to refresh manually

#!/usr/bin/env /usr/local/bin/node

/* jshint esversion: 6 */

/*
 * <bitbar.title>Track Brazilian Post Office (Correios) packages</bitbar.title>
 * <bitbar.version>v1.0</bitbar.version>
 * <bitbar.author>Carlos E. Torres</bitbar.author>
 * <bitbar.author.github>cetorres</bitbar.author.github>
 * <bitbar.desc>EN: Track Brazilian Post Office (Correios) packages. PT: Rastreie encomendas dos Correios.</bitbar.desc>
 * <bitbar.abouturl>https://github.com/cetorres/bitbar-track-correios-packages</bitbar.abouturl>
 * <bitbar.image>https://raw.githubusercontent.com/cetorres/bitbar-track-correios-packages/master/screenshot.png</bitbar.image>
 * <bitbar.dependencies>node</bitbar.dependencies>
 */

// Enter the track code here.
// Informe o código de rastreio aqui.
var code = ""; // Ex.: AA123456789BR

// Don't change anything else below this line.
// Não altere mais nada a partir desta linha.
const fs = require('fs');
const https = require('https');
var url = "https://wsmobile.correios.com.br/service/rest/rastro/rastroMobile/FALECONOSC/7KMJ6FISA4/L/T/" + code + "/101";
var logFile = ".track-correios-package.log";

var getLogData = function(callback) {
    fs.readFile(__dirname + '/' + logFile, (e, data) => {
        if (e) {
            callback(null);
        }
        callback(data);
    });
};

var saveLogData = function(data) {
    fs.writeFile(__dirname + '/' + logFile, data, function(err) {
        if (err) {
            console.log(err);
        }
    });
};

var refreshMenu = function() {
    console.log("---");
    console.log("Atualizar | refresh=true");
};

var aboutMenu = function() {
    console.log('---');
    console.log('by Carlos E. Torres | href=http://cetorres.com');
};

const toTitleCase = (phrase) => {    
    return phrase ? phrase
        .toLowerCase()
        .split(' ')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(' ') : "";
};

if (!fs.existsSync(__dirname + '/' + logFile)) {
    saveLogData(0);
}

https.get(url, (res) => {

    res.setEncoding('utf-8');
    var responseString = '';

    res.on('data', function(data) {
        responseString += data;
    });

    res.on('end', function() {       
        var responseObject = JSON.parse(responseString);
        var nomeObjeto = responseObject.objeto[0].nome != undefined ? responseObject.objeto[0].nome : "OBJETO";
        var eventos = responseObject.objeto[0].evento;
        var eventosTotal = eventos != undefined ? eventos.length : 0;

        if (nomeObjeto != "OBJETO" && eventosTotal == 0) {
            console.log("📦 0 | color=yellow");
            console.log("---");
            console.log(nomeObjeto + "\n");
            console.log("Código: " + code);
            console.log("---");
            console.log("Sem eventos no momento.");      

            refreshMenu();
            aboutMenu(); 
        }
        else if (nomeObjeto == "OBJETO" && eventosTotal == 0) {
            console.log("📦 0 | color=yellow");
            console.log("---");
            console.log(nomeObjeto + "\n");
            console.log("Código: " + code);
            console.log("---");
            console.log("Objeto não encontrado."); 

            refreshMenu();
            aboutMenu(); 
        }
        else if (nomeObjeto != "" && eventosTotal > 0) {
            var header = eventos.filter(evento => evento.tipo == "PO")[0];            

            getLogData((data) => {
                var newEvents = false;
                var eventosLog = data;

                if (eventosLog != undefined) {
                    if (eventosLog != eventosTotal) {
                        newEvents = true;
                    }
                }            

                saveLogData(eventosTotal);

                var objetoEntregue = eventos.find(e => e.tipo == "BDE") != undefined ? true : false;

                if (objetoEntregue) {
                    console.log("📦 ✓ | color=green");    
                }
                else {
                    console.log("📦 " + eventosTotal + (newEvents ? ' | color=green' : ''));
                }
                console.log("---");            
                console.log(nomeObjeto + "\n");
                console.log("Código: " + code);
                console.log('Postado em: ' + (header == undefined ? eventos[0].dataPostagem : header.postagem.datapostagem));

                eventos.forEach(evento => {                
                    console.log("---");
                    console.log(evento.data + " - " + evento.hora + "\n");                 
                    if (evento.tipo == "RO") {            
                        console.log(evento.descricao.trim() + "\n");                                                        
                        console.log("Para: " + toTitleCase(evento.destino[0].local) + (evento.destino[0].cidade != undefined ? (" em " + toTitleCase(evento.destino[0].cidade)) : ""));
                        console.log("De: " + toTitleCase(evento.unidade.local) + (evento.unidade.cidade != undefined ? (" em " + toTitleCase(evento.unidade.cidade) + "\n") : ""));   
                    } 
                    else if (evento.tipo == "PAR") {
                        console.log(evento.descricao.trim() + "\n");                                                        
                        console.log(toTitleCase(evento.unidade.local));
                    }
                    else {
                        console.log(evento.descricao.trim() + "\n");
                        if (evento.detalhe != undefined) {
                            console.log(evento.detalhe.trim() + "\n");                                
                        }                        
                        if (evento.postagem != undefined) {
                            console.log("Para: " + toTitleCase(evento.postagem.destinatario));
                        }
                        console.log("De: " + toTitleCase(evento.unidade.local) + (evento.unidade.cidade != undefined ? (" em " + toTitleCase(evento.unidade.cidade) + "\n") : "")); 
                    }               
                });

                console.log("---");
                console.log("Ver online | href=" + url); 

                refreshMenu();
                aboutMenu(); 
            });                 

        }

    });

})
.on('error', (e) => {
    console.log('✖︎ | color=red');
    console.log('---');
    console.log('Erro ao ler dados do serviço dos Correios.\n' + e);
    refreshMenu();
    aboutMenu();
});
