#!/bin/bash

# <bitbar.title>Caffeinate</bitbar.title>
# <bitbar.version>v0.2</bitbar.version>
# <bitbar.author>Steffen Froehlich</bitbar.author>
# <bitbar.author.github>citoki</bitbar.author.github>
# <bitbar.desc>This plugin will give some caffeine, with lots of sugar, to your Mac to stay awake.
# Technically the commandline tool 'caffeinate' is executed.</bitbar.desc>
# <bitbar.image>https://i.imgur.com/vsCWLwX.png</bitbar.image>
# <bitbar.dependencies></bitbar.dependencies>


function get_program_state {
  instances=$(pgrep caffeinate)

  # set state
  if [[ $instances = 1 ]]; then
    state="stopped";
  else
    state="running";
  fi
}

function set_program_icon {
  get_program_state;

  if [[ "$state" = "stopped" ]]; then
    icon="☕️";
  else
    icon="🍵";
  fi
}

# stop all previous processes
function terminate_caffeinate_instances {
  /usr/bin/killall caffeinate &> /dev/null
}

if [[ "$1" = "caffeine" ]]; then
  terminate_caffeinate_instances;
  # start caffeinate program and prevent the system and display
  # from idle sleeping
  /usr/bin/caffeinate -di;

  exit
fi

if [[ "$1" = "stop" ]]; then
  terminate_caffeinate_instances;

  exit
fi

# set program icon to default: 'not active state'
set_program_icon;

echo "$icon"
echo "---"
echo "program state: ${state}"
echo "---"
echo "Prevent machine from idle sleep | bash='$0' param1=caffeine terminal=false"
echo "Stop program | bash='$0' param1=stop terminal=false"

#!/usr/bin/env python
# coding=utf-8
# <bitbar.title>Screenshot</bitbar.title>
# <bitbar.version>v1.1</bitbar.version>
# <bitbar.author>Brandon Barker, Soumya Ranjan Mohanty</bitbar.author>
# <bitbar.author.github>ProjectBarks, geekysrm</bitbar.author.github>
# <bitbar.desc>Allows for screenshots to be uploaded, saved, and added to the clipboard</bitbar.desc>
# <bitbar.image>http://i.imgur.com/51rg3EJ.png</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>
# <bitbar.abouturl>https://github.com/matryer/bitbar-plugins/blob/master/System/screenshot.1d.py</bitbar.abouturl>

import os, subprocess, tempfile, hashlib, requests, sys, platform, time
from distutils.version import StrictVersion

SAVE_PATH = "~/Pictures/"


def screenshot(path, copy_to_clipboard=False, show_cursor=False, show_errors=False, interactive=False,
               only_main_monitor=False, window_mode=False, open_in_preview=False, selection_mode=True,
               sounds=True, delay=5):
    params = ""
    if copy_to_clipboard:
        params += "-c "
    if show_cursor:
        params += "-C "
    if show_errors:
        params += "-d "
    if interactive:
        params += "-i "
    if only_main_monitor:
        params += "-m "
    if window_mode:
        params += "-o "
    if open_in_preview:
        params += "-P "
    if selection_mode:
        params += "-s "
    if sounds:
        params += "-x "
    if delay != 5 and delay >= 0:
        params += "-T {} ".format(delay)

    os.system("screencapture {} {}".format(params, path))
    return os.path.isfile(path)


def text_to_clipboard(output):
    process = subprocess.Popen('pbcopy', env={'LANG': 'en_US.UTF-8'}, stdin=subprocess.PIPE)
    process.communicate(output.encode('utf-8'))


def notify(title, subtitle, message):
    command = "display notification \"{}\" with title \"{}\"".format(message, title)
    if len(subtitle) > 0:
        command += " subtitle \"{}\"".format(subtitle)
    os.system("osascript -e '{}'".format(command))


def upload_image(upload):
    # upload is the path of the image
    headers = {
        'authorization': 'Client-ID 6fcd294cd0e8aa1',
    }  
    files = {
        'image': (open(upload, 'rb')),
    }
    r = requests.post('https://api.imgur.com/3/upload', headers=headers, files=files)
    import json
    return json.loads(r.text)['data']['link']

class Command(object):
    def __init__(self, title, name):
        self.title = title
        self.name = name

    def get_name(self):
        return self.name

    def get_description(self):
        return "{0} |bash={2} param1={1} terminal=false".format(self.title, self.name, os.path.realpath(__file__))

    def execute(self):
        raise Exception("Abstract Function")


class Upload(Command):
    def __init__(self):
        super(Upload, self).__init__("Upload Online", "upload")

    def execute(self):
        temp_path = tempfile.NamedTemporaryFile().name + ".png"
        if not screenshot(temp_path):
            exit()
        notify("Uploading Screenshot", "", "Your image is being uploaded online!")
        url = upload_image(temp_path)
        os.remove(temp_path)
        notify("Copied Screenshot", "", "Image URL copied to clipboard!")
        text_to_clipboard(url)


class Clipboard(Command):
    def __init__(self):
        super(Clipboard, self).__init__("Copy to Clipboard", "clipboard")

    def execute(self):
        temp_path = tempfile.NamedTemporaryFile().name + ".png"
        if not screenshot(temp_path):
            exit()
        os.system("osascript -e 'set the clipboard to POSIX file \"{}\"'".format(temp_path))
        notify("Copied Screenshot", "", "Image copied to clipboard!")


class SaveFile(Command):
    def __init__(self):
        super(SaveFile, self).__init__("Save to File", "save")

    def execute(self):
        temp_path = os.path.join(os.path.expanduser(SAVE_PATH), time.strftime("screenshot-%Y%m%d-%H%M%S.png"))
        parent = os.path.dirname(temp_path)
        if not os.path.isdir(parent):
            os.mkdir(parent)
        if not screenshot(temp_path):
            exit()
        os.system("open -R {0}".format(temp_path))


version = platform.mac_ver()[0]
if StrictVersion(version) < StrictVersion("10.9"):
    raise Exception("Mac OSX is too old!")

sub_commands = [Upload(), Clipboard(), SaveFile()]

if len(sys.argv) <= 1:
    print("📸")
    print("---")
    for sub_command in sub_commands:
        print(sub_command.get_description())
else:
    try:
        for sub_command in sub_commands:
            if sub_command.get_name() != sys.argv[1]:
                continue
            sub_command.execute()
    except Exception, e:
        notify("Error", "", str(e))

#!/usr/bin/env bash

# <bitbar.title>Top 5 Memory-Consuming Applications</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>
# <bitbar.author>Qiansen Y</bitbar.author>
# <bitbar.author.github>hypersport</bitbar.author.github>
# <bitbar.desc>top 5 memory-consuming applications</bitbar.desc>
# <bitbar.image>http://23.105.211.229/images/top5-memory-consuming.jpg</bitbar.image>
# <bitbar.about>top 5 memory-consuming applications</bitbar.about>

ps xmo rss=,pmem=,comm= | while read -r rss pmem comm; ((n++<5)); do
size="$((rss/1024))";
short=$((4-${#size}));
size="(${size}M)";
i=0;
while ((i++ < short)); do size=" $size"; done;
pmem="${pmem%%.*}"
if [ "$pmem" -ge 20 ]; then color=$'\e[31m';
elif [ "$pmem" -ge 10 ]; then color=$'\e[33m';
else color=$'\e[32m';
fi
echo "$color$pmem% $size $(basename "$comm")"$'\e[0m'"";
done

#!/usr/bin/env xcrun swift

/*
 * <bitbar.title>Trackpad Dot</bitbar.title>
 * <bitbar.version>v1.0</bitbar.version>
 * <bitbar.author>Manu Wallner</bitbar.author>
 * <bitbar.author.github>milch</bitbar.author.github>
 * <bitbar.desc>Displays a coloured dot in the menu bar that gradually changes from green to yellow to red the lower your Magic Trackpad's battery</bitbar.desc>
 * <bitbar.image>http://i.imgur.com/4G7B76u.png</bitbar.image>
 * <bitbar.dependencies>Xcode,swift</bitbar.dependencies>
 */

import Foundation

typealias Color = (red : UInt, green : UInt, blue : UInt)

func shell(_ args: String...) -> String {
    let task = Process()
    task.launchPath = "/bin/bash"
    task.arguments = ["-c"] + args

    let pipe = Pipe()
    task.standardOutput = pipe
    task.launch()

    let data = pipe.fileHandleForReading.readDataToEndOfFile()
    let output: String = NSString(data: data, encoding: String.Encoding.utf8.rawValue) as! String

    return output.trimmingCharacters(in: CharacterSet.whitespacesAndNewlines)
}

func rgb(_ r : UInt, _ g : UInt , _ b : UInt) -> Color {
    return (red: r, green: g, blue: b)
}

func linear(_ initial: UInt, next: UInt, percent: Double) -> Double {
    return Double(initial) * (1.0 - percent) + Double(next) * (percent)
}

func interpolate(_ first : Color, second : Color, percent: Double) -> Color {
    let r = linear(first.red, next: second.red, percent: percent)
    let g = linear(first.green, next: second.green, percent: percent)
    let b = linear(first.blue, next: second.blue, percent: percent)
    return rgb(UInt(r), UInt(g), UInt(b))
}

func componentToHex(_ component : UInt) -> String {
    return String(format:"%02X", component)
}

func colorToHex(_ color: Color) -> String {
    return [
        "#",
        componentToHex(color.red),
        componentToHex(color.green),
        componentToHex(color.blue)
        ].joined(separator: "")
}

let commandString = "ioreg -c BNBTrackpadDevice | egrep -o 'BatteryPercent. = \\d{0,3}' | sed -E 's/[^0-9]+([0-9]{0,3})/\\1/g'"
let trackpadPercentString = shell(commandString)
var trackpadDropdownString = "\(trackpadPercentString)%"
var colorString = ""

let colors = [
    rgb(231, 76, 60),
    rgb(241, 196, 15),
    rgb(0, 177, 106)
]

if let trackpadPercent = Int(trackpadPercentString) {
    var interpolationColors : (first: Color, second: Color) = (first: rgb(0,0,0), second: rgb(0,0,0))

    switch(trackpadPercent) {
    case 0...50: interpolationColors = (first: colors[0], second: colors[1])
    case 50...100: interpolationColors = (first: colors[1], second: colors[2])
    // Catch all to satisfy the compiler
    default: break
    }
    let percent = trackpadPercent % 50 == 0 ? 1.0 : Double(trackpadPercent % 50) / 50.0
    let color = interpolate(interpolationColors.first, second: interpolationColors.second, percent: percent)
    colorString = colorToHex(color)
} else {
    colorString = "#bdc3c7"
    trackpadDropdownString = "Not connected"
}

print("● | color=\(colorString)")
print("---")
print("Trackpad: \(trackpadDropdownString)")

#!/usr/local/bin/python3

# <bitbar.title>Watt Meter</bitbar.title>
# <bitbar.version>v1.0.0</bitbar.version>
# <bitbar.author>Eric Ripa</bitbar.author>
# <bitbar.author.github>eripa</bitbar.author.github>
# <bitbar.desc>Show current watt drain, tries to be smart and highlight high power usage</bitbar.desc>
# <bitbar.image>http://i.imgur.com/blj2KCP.png</bitbar.image>
# <bitbar.dependencies>python3</bitbar.dependencies>

from plistlib import loads
import sys
import subprocess

# Currently only rough guestimates. Only somewhat tested number on MacBook Air.abs
# Feel free to update/tweak etc..
IMPACT = {
    2: {
        "MacBook Air": {
            "high": 20,
            "low": 10
        },
        "MacBook Pro": {
            "high": 50,
            "low": 20
        }
    },
    4: {
        "MacBook Pro": {
            "high": 70,
            "low": 30
        }
    },
    6: {
        "MacBook Pro": {
            "high": 80,
            "low": 30
        }
    }
}

def parse_system_profiler():
    output = subprocess.check_output(["/usr/sbin/system_profiler", \
                                     "-xml", "SPPowerDataType", "SPHardwareDataType"])
    plist = loads(output)
    spbattery_info = plist[0]['_items'][0]

    machine = {
        "current_amperage": int(spbattery_info['sppower_current_amperage']),
        "current_voltage": int(spbattery_info['sppower_current_voltage']),
        "current_watt": abs(int(spbattery_info['sppower_current_voltage']) * \
            int(spbattery_info['sppower_current_amperage'])/1000/1000),
        "model": plist[1]['_items'][0]['machine_name'],
        "cores": plist[1]['_items'][0]['number_processors'],
    }
    return machine

def get_impact(machine):
    model = machine['model']
    cores = machine['cores']
    watt = machine['current_watt']
    low = IMPACT[cores][model]['low']
    high = IMPACT[cores][model]['high']
    if watt <= low:
        return "low"
    if watt >= high:
        return "high"
    return "mid"

def main():
    machine = parse_system_profiler()
    impact = get_impact(machine)
    color = "gray"
    if impact == "low":
        color = "green"
    if impact == "high":
        color = "red"
    refresh_interval = sys.argv[0].split('.')[2]
    print("%.1fW | color=%s" % (machine['current_watt'], color))
    print("---")
    print("Refresh (current interval: %s) | refresh=true" % refresh_interval)

if __name__ == '__main__':
    main()

